	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'BusinessCombinationsMember' ,N'Business combinations [member]', N'This member stands for transactions or other events in which an acquirer obtains control of one or more businesses. Transactions sometimes referred to as ''true mergers'' or ''mergers of equals'' are also business combinations as that term is used in Ifrs 3.');
