	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContingentLiabilitiesMember' ,N'Contingent liabilities [member]', N'This member stands for possible obligations that arise from past events and whose existence will be confirmed only by the occurrence or non-occurrence of one or more uncertain future events not wholly within the control of the entity; or, present obligations that arise from past events but are not recognised because (a) it is probable that an outflow of resources embodying economic benefits will be required to settle the obligations; or (b) the amount of the obligations cannot be measured with sufficient reliability. It also represents the standard value for the ''Classes of contingent liabilities'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WarrantyContingentLiabilityMember' ,N'Warranty contingent liability [member]', N'This member stands for a contingent liability for estimated costs of making good under warranties for products sold. [Refer: Contingent liabilities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RestructuringContingentLiabilityMember' ,N'Restructuring contingent liability [member]', N'This member stands for a contingent liability for restructuring, such as the sale or termination of a line of business; closure of business locations in a country or region or relocation of activities from one country or region to another; changes in management structure; and fundamental reorganisations that have a material effect on the nature and focus of the entity''s operations. [Refer: Contingent liabilities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LegalProceedingsContingentLiabilityMember' ,N'Legal proceedings contingent liability [member]', N'This member stands for a contingent liability for legal proceedings. [Refer: Contingent liabilities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OnerousContractsContingentLiabilityMember' ,N'Onerous contracts contingent liability [member]', N'This member stands for a contingent liability for onerous contracts. An onerous contract is a contract in which the unavoidable costs of meeting the obligation under the contract exceed the economic benefits expected to be received under it. [Refer: Contingent liabilities [member]]');
