	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'KeyManagementPersonnelOfEntityOrParentMember' ,N'Key management personnel of entity or parent [member]', N'This member stands for those persons having authority and responsibility for planning, directing and controlling the activities of the entity, directly or indirectly, including any director (whether executive or otherwise) of that entity.');
