	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsArisingFromDerecognitionOfFinancialAssetsMeasuredAtAmortisedCost' ,N'Gains arising from derecognition of financial assets measured at amortised cost', N'The gain, recognised in the statement of comprehensive income, that arises from the derecognition of financial assets measured at amortised cost. [Refer: Financial assets at amortised cost]');
