	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TypesOfHedgesMember' ,N'Hedges [member]', N'This member stands for all types of hedges. It also represents the standard value for the ''Types of hedges'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FairValueHedgesMember' ,N'Fair value hedges [member]', N'This member stands for hedges of the exposure to changes in fair value of a recognised asset or liability or an unrecognised firm commitment, or an identified portion of such an asset, liability or firm commitment, that is attributable to a particular risk and could affect profit or loss. [Refer: Hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CashFlowHedgesMember' ,N'Cash flow hedges [member]', N'This member stands for hedges of the exposure to variability in cash flows that (a) are attributable to a particular risk associated with a recognised asset or liability (such as all or some future interest payments on variable rate debt) or a highly probable forecast transaction; and (b) could affect profit or loss. [Refer: Hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'HedgesOfNetInvestmentInForeignOperationsMember' ,N'Hedges of net investment in foreign operations [member]', N'This member stands for hedges of the entity''s monetary items that are receivable from or payable to a foreign operation, for which settlement is neither planned nor likely to occur in the foreseeable future. A foreign operation is an entity that is a subsidiary, associate, joint venture or branch of a reporting entity, the activities of which are based or conducted in a country or currency other than those of the reporting entity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfHedgeAccountingLineItems' ,N'Disclosure of detailed information about hedges [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTypeOfHedge' ,N'Description of type of hedge', N'The description of the type of a hedge used by the entity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfFinancialInstrumentsDesignatedAsHedgingInstrument' ,N'Description of financial instruments designated as hedging instruments', N'The description of financial instruments designated as hedging instruments. Hedging instruments are designated derivatives or (for a hedge of the risk of changes in foreign currency exchange rates only) designated non-derivative financial assets or non-derivative financial liabilities whose fair value or cash flows are expected to offset changes in the fair value or cash flows of a designated hedged item. [Refer: Derivatives [member]; Derivative financial assets; Derivative financial liabilities; Financial instruments, class [member]; Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialInstrumentsDesignatedAsHedgingInstrumentsAtFairValue' ,N'Financial instruments designated as hedging instruments, at fair value', N'The fair value of financial instruments designated as hedging instruments. Hedging instruments are designated derivatives or (for a hedge of the risk of changes in foreign currency exchange rates only) designated non-derivative financial assets or non-derivative financial liabilities whose fair value or cash flows are expected to offset changes in the fair value or cash flows of a designated hedged item. [Refer: At fair value [member]; Derivatives [member]; Derivative financial assets; Derivative financial liabilities; Financial instruments, class [member]; Financial assets; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfRisksBeingHedged' ,N'Description of nature of risks being hedged', N'The description of the nature of risks being hedged.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfPeriodsWhenCashFlowsExpectedToOccur' ,N'Description of periods when cash flows expected to occur', N'The description of periods when cash flows are expected to occur for cash flow hedges. [Refer: Cash flow hedges [member]]');
