	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsLossesOnHedgedItemAttributableToHedgedRisk' ,N'Gains (losses) on hedged item attributable to hedged risk, fair value hedges', N'The gains (losses) on hedged items in fair value hedges, which are attributable to the hedged risk. [Refer: Fair value hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsLossesOnIneffectivenessOfCashFlowHedgesRecognisedInProfitOrLoss' ,N'Gains (losses) on ineffectiveness of cash flow hedges recognised in profit or loss', N'The gains (losses) on the ineffective portion of cash flow hedges recognised in profit or loss. [Refer: Cash flow hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsLossesOnIneffectivenessOfHedgesOfNetInvestmentsInForeignOperations' ,N'Gains (losses) on ineffectiveness of hedges of net investments in foreign operations recognised in profit or loss', N'The gains (losses) on the ineffective portion of hedges of net investments in foreign operations recognised in profit or loss. [Refer: Hedges of net investment in foreign operations [member]]');
