	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TypesOfHedgesMember' ,N'Hedges [member]', N'This member stands for all types of hedges. It also represents the standard value for the ''Types of hedges'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FairValueHedgesMember' ,N'Fair value hedges [member]', N'This member stands for hedges of the exposure to changes in fair value of a recognised asset or liability or an unrecognised firm commitment, or an identified portion of such an asset, liability or firm commitment, that is attributable to a particular risk and could affect profit or loss. [Refer: Hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CashFlowHedgesMember' ,N'Cash flow hedges [member]', N'This member stands for hedges of the exposure to variability in cash flows that (a) are attributable to a particular risk associated with a recognised asset or liability (such as all or some future interest payments on variable rate debt) or a highly probable forecast transaction; and (b) could affect profit or loss. [Refer: Hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'HedgesOfNetInvestmentInForeignOperationsMember' ,N'Hedges of net investment in foreign operations [member]', N'This member stands for hedges of the entity''s monetary items that are receivable from or payable to a foreign operation, for which settlement is neither planned nor likely to occur in the foreseeable future. A foreign operation is an entity that is a subsidiary, associate, joint venture or branch of a reporting entity, the activities of which are based or conducted in a country or currency other than those of the reporting entity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'HedgingInstrumentsAxis' ,N'Hedging instruments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'HedgingInstrumentsMember' ,N'Hedging instruments [member]', N'This member stands for hedging instruments. A hedging instrument can be a designated: (a) derivative measured at fair value through profit or loss, except for some written options (see paragraph B6.2.4 of Ifrs 9); (b) non-derivative financial asset or non-derivative financial liability measured at fair value through profit or loss, unless it is a financial liability designated as at fair value through profit or loss for which the amount of its change in fair value that is attributable to changes in the credit risk of that liability is presented in other comprehensive income in accordance with paragraph 5.7.7 of Ifrs 9. For a hedge of foreign currency risk, the foreign currency risk component of a non-derivative financial asset or a non-derivative financial liability may be designated as a hedging instrument provided that it is not an investment in an equity instrument for which an entity has elected to present changes in fair value in other comprehensive income in accordance with paragraph 5.7.5 of Ifrs 9. This member also represents the standard value for the ''Hedging instruments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDetailedInformationAboutHedgingInstrumentsLineItems' ,N'Disclosure of detailed information about hedging instruments [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
