	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'HedgingInstrumentLiabilities' ,N'Hedging instrument, liabilities', N'The amount of a hedging instrument, recognised as a liability. [Refer: Hedging instruments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfLineItemInStatementOfFinancialPositionThatIncludesHedgingInstrument' ,N'Description of line item in statement of financial position that includes hedging instrument', N'The description of the line item in the statement of financial position that includes the hedging instrument. [Refer: Hedging instruments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainLossOnChangeInFairValueOfHedgingInstrumentUsedAsBasisForRecognisingHedgeIneffectiveness' ,N'Gain (loss) on change in fair value of hedging instrument used as basis for recognising hedge ineffectiveness', N'The gain (loss) on the change in fair value of the hedging instrument used as a basis for recognising hedge ineffectiveness. Hedge ineffectiveness is the extent to which the changes in the fair value or the cash flows of the hedging instrument are greater or less than those on the hedged item.');
