	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MarketRiskMember' ,N'Market risk [member]', N'This member stands for the risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in market prices. Market risk comprises three types of risk: currency risk, interest rate risk and other price risk. [Refer: Currency risk [member]; Interest rate risk [member]; Other price risk [member]; Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrencyRiskMember' ,N'Currency risk [member]', N'This member stands for a type of market risk representing the risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in foreign exchange rates. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InterestRateRiskMember' ,N'Interest rate risk [member]', N'This member stands for the type of risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in market interest rates. [Refer: Financial instruments, class [member]]');
