	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ResidualValueRiskMember' ,N'Residual value risk [member]', N'This member stands for a component of other price risk that represents the type of risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in residual values. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RiskDiversificationEffectMember' ,N'Risk diversification effect [member]', N'This member stands for the effect of the diversification of risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfNatureAndExtentOfRisksArisingFromFinancialInstrumentsLineItems' ,N'Disclosure of nature and extent of risks arising from financial instruments [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfExposureToRisk' ,N'Description of exposure to risk', N'The description of exposures to risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfObjectivesPoliciesAndProcessesForManagingRisk' ,N'Description of objectives, policies and processes for managing risk', N'The description of objectives, policies and processes for managing the risks that arise from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MethodsUsedToMeasureRisk' ,N'Methods used to measure risk', N'The description of the methods used to measure risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ChangesInExposureToRisk' ,N'Description of changes in exposure to risk', N'The description of changes in the exposure to risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ChangesInObjectivesPoliciesAndProcessesForManagingRisk' ,N'Description of changes in objectives, policies and processes for managing risk', N'The description of changes in objectives, policies and processes for managing risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ChangesInMethodsUsedToMeasureRisk' ,N'Description of changes in methods used to measure risk', N'The description of changes in methods used to measure risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SummaryQuantitativeDataAboutEntitysExposureToRisk' ,N'Summary quantitative data about entity''s exposure to risk [text block]', N'The disclosure of summary quantitative data about the entity''s exposure to risks arising from financial instruments. This disclosure shall be based on the information provided internally to key management personnel of the entity, for example, the entity’s board of directors or chief executive officer. [Refer: Financial instruments, class [member]; Key management personnel of entity or parent [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ConcentrationsOfRisk' ,N'Description of concentrations of risk', N'The description of concentrations of risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfHowManagementDeterminesConcentrations' ,N'Description of how management determines concentrations', N'The description of how management determines concentrations of risks arising from financial instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfSharedCharacteristicForConcentration' ,N'Description of shared characteristic for concentration', N'The description of a shared characteristic for the concentration of risks arising from financial instruments (for example, counterparty, geographical area, currency or market). [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RiskExposureAssociatedWithInstrumentsSharingCharacteristic' ,N'Risk exposure associated with instruments sharing characteristic', N'The amount of risk exposure associated with financial instruments with a shared characteristic that identifies a concentration of risks. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AdditionalInformationAboutEntityExposureToRisk' ,N'Additional information about entity exposure to risk', N'Additional information about the entity''s exposure to risk when the quantitative data disclosed are unrepresentative.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SensitivityAnalysisForEachTypeOfMarketRisk' ,N'Sensitivity analysis for types of market risk [text block]', N'The disclosure of the sensitivity analysis for types of market risk to which the entity is exposed, showing how profit or loss and equity would have been affected by changes in the relevant risk variable that were reasonably possible at that date. [Refer: Market risk [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfCreditRiskExplanatory' ,N'Disclosure of credit risk [text block]', N'The disclosure of credit risk. [Refer: Credit risk [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfCrossreferenceToDisclosuresAboutCreditRiskPresentedOutsideFinancialStatements' ,N'Description of cross-reference to disclosures about credit risk presented outside financial statements', N'The description of cross-reference to disclosures about credit risk presented outside the financial statements.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfCreditRiskManagementPracticesAndHowTheyRelateToRecognitionAndMeasurementOfExpectedCreditLossesExplanatory' ,N'Explanation of credit risk management practices and how they relate to recognition and measurement of expected credit losses [text block]', N'The explanation of the credit risk management practices and how they relate to the recognition and measurement of expected credit losses.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationOnHowEntityDeterminedWhetherCreditRiskOfFinancialInstrumentsHasIncreasedSignificantlySinceInitialRecognition' ,N'Information on how entity determined whether credit risk of financial instruments has increased significantly since initial recognition', N'Information on how an entity determined whether the credit risk of financial instruments has increased significantly since initial recognition, including if and how: (a) financial instruments are considered to have low credit risk; and (b) the presumption that there have been significant increases in credit risk since initial recognition when financial assets are more than 30 days past due, has been rebutted. [Refer: Credit risk [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutEntitysDefinitionsOfDefault' ,N'Information about entity''s definitions of default', N'Information about an entity''s definitions of default, including the reasons for selecting those definitions.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationOnHowInstrumentsWereGroupedIfExpectedCreditLossesWereMeasuredOnCollectiveBasis' ,N'Information on how instruments were grouped if expected credit losses were measured on collective basis', N'Information on how the instruments were grouped if expected credit losses were measured on a collective basis.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationOnHowEntityDeterminedThatFinancialAssetsAreCreditimpairedFinancialAssets' ,N'Information on how entity determined that financial assets are credit-impaired financial assets', N'Information on how an entity determined that financial assets are credit-impaired financial assets.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationOnEntitysWriteoffPolicy' ,N'Information on entity''s write-off policy', N'Information on an entity''s write-off policy, including the indicators that there is no reasonable expectation of recovery and information about the policy for financial assets that are written-off but are still subject to enforcement activity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationOnHowRequirementsForModificationOfContractualCashFlowsOfFinancialAssetsHaveBeenApplied' ,N'Information on how requirements for modification of contractual cash flows of financial assets have been applied', N'Information on how the requirements for the modification of contractual cash flows of financial assets have been applied, including how an entity: (a) determines whether the credit risk on a financial asset that has been modified while the loss allowance was measured at an amount equal to lifetime expected credit losses, has improved to the extent that the loss allowance reverts to being measured at an amount equal to 12-month expected credit losses; and (b) monitors the extent to which the loss allowance on financial assets meeting the criteria in (a) is subsequently remeasured at an amount equal to lifetime expected credit losses.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfInputsAssumptionsAndEstimationTechniquesUsedToApplyImpairmentRequirementsExplanatory' ,N'Explanation of inputs, assumptions and estimation techniques used to apply impairment requirements [text block]', N'The explanation of the inputs, assumptions and estimation techniques used to apply the impairment requirements for financial instruments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOfInputsAndAssumptionsAndEstimationTechniquesUsedToMeasure12monthAndLifetimeExpectedCreditLosses' ,N'Description of basis of inputs and assumptions and estimation techniques used to measure 12-month and lifetime expected credit losses', N'The description of the basis of inputs and assumptions and the estimation techniques used to measure the 12-month and lifetime expected credit losses.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOfInputsAndAssumptionsAndEstimationTechniquesUsedToDetermineWhetherCreditRiskOfFinancialInstrumentsHaveIncreasedSignificantlySinceInitialRecognition' ,N'Description of basis of inputs and assumptions and estimation techniques used to determine whether credit risk of financial instruments have increased significantly since initial recognition', N'The description of the basis of inputs and assumptions and the estimation techniques used to determine whether the credit risk of financial instruments have increased significantly since initial recognition.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOfInputsAndAssumptionsAndEstimationTechniquesUsedToDetermineWhetherFinancialAssetIsCreditimpairedFinancialAsset' ,N'Description of basis of inputs and assumptions and estimation techniques used to determine whether financial asset is credit-impaired financial asset', N'The description of the basis of inputs and assumptions and the estimation techniques used to determine whether a financial asset is a credit-impaired financial asset.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfHowForwardlookingInformationHasBeenIncorporatedIntoDeterminationOfExpectedCreditLosses' ,N'Description of how forward-looking information has been incorporated into determination of expected credit losses', N'The description of how forward-looking information has been incorporated into the determination of expected credit losses, including the use of macroeconomic information.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfChangesInEstimationTechniquesOrSignificantAssumptionsMadeWhenApplyingImpairmentRequirementsAndReasonsForThoseChanges' ,N'Description of changes in estimation techniques or significant assumptions made when applying impairment requirements and reasons for those changes', N'The description of changes in the estimation techniques or significant assumptions made when applying impairment requirements and the reasons for those changes.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfChangesInLossAllowanceAndExplanationOfChangesInGrossCarryingAmountForFinancialInstrumentsExplanatory' ,N'Disclosure of reconciliation of changes in loss allowance and explanation of changes in gross carrying amount for financial instruments [text block]', N'The disclosure of the reconciliation of changes in the loss allowance and explanation of changes in the gross carrying amount for financial instruments. Loss allowance is the allowance for expected credit losses on financial assets measured in accordance with paragraph 4.1.2 of Ifrs 9, lease receivables and contract assets, the accumulated impairment amount for financial assets measured in accordance with paragraph 4.1.2A of Ifrs 9 and the provision for expected credit losses on loan commitments and financial guarantee contracts. [Refer: Gross carrying amount [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfChangesInLossAllowanceAndExplanationOfChangesInGrossCarryingAmountForFinancialInstrumentsTable' ,N'Disclosure of reconciliation of changes in loss allowance and explanation of changes in gross carrying amount for financial instruments [table]', N'Schedule disclosing information related to the reconciliation of changes in the loss allowance and explanation of changes in the gross carrying amount for financial instruments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ClassesOfFinancialInstrumentsAxis' ,N'Classes of financial instruments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ClassesOfFinancialInstrumentsMember' ,N'Financial instruments, class [member]', N'This member stands for aggregated classes of financial instruments. Financial instruments are contracts that give rise to a financial asset of one entity and a financial liability or equity instrument of another entity. It also represents the standard value for the ''Classes of financial instruments'' axis if no other member is used. [Refer: Financial assets; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LoanCommitmentsMember' ,N'Loan commitments [member]', N'This member stands for firm commitments to provide credit under pre-specified terms and conditions.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialGuaranteeContractsMember' ,N'Financial guarantee contracts [member]', N'This member stands for contracts that require the issuer to make specified payments to reimburse the holder for a loss it incurs because a specified debtor fails to make payment when due in accordance with the original or modified terms of a debt instrument.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TradeReceivablesMember' ,N'Trade receivables [member]', N'This member stands for trade receivables. [Refer: Trade receivables]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContractAssetsMember' ,N'Contract assets [member]', N'This member stands for contract assets. [Refer: Contract assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LeaseReceivablesMember' ,N'Lease receivables [member]', N'This member stands for receivables related to leases.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MortgagesMember' ,N'Mortgages [member]', N'This member stands for a class of financial assets that represents loans to finance the purchase of assets where the asset acts as collateral to secure the loan.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ConsumerLoansMember' ,N'Loans to consumers [member]', N'This member stands for loans that are made to individuals for personal use.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CorporateLoansMember' ,N'Loans to corporate entities [member]', N'This member stands for loans made to corporate entities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LoansToGovernmentMember' ,N'Loans to government [member]', N'This member stands for loans made to government.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TypeOfMeasurementOfExpectedCreditLossesAxis' ,N'Type of measurement of expected credit losses [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TypeOfMeasurementOfExpectedCreditLossesMember' ,N'Type of measurement of expected credit losses [member]', N'This member stands for all types of measurement of expected credit losses. Expected credit losses are the weighted average of credit losses with the respective risks of a default occurring as the weights. This member also represents the standard value for the ''Type of measurement of expected credit losses'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TwelvemonthExpectedCreditLossesMember' ,N'12-month expected credit losses [member]', N'This member stands for the portion of lifetime expected credit losses that represent the expected credit losses that result from default events on a financial instrument that are possible within the 12 months after the reporting date. [Refer: Type of measurement of expected credit losses [member]; Lifetime expected credit losses [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LifetimeExpectedCreditLossesMember' ,N'Lifetime expected credit losses [member]', N'This member stands for the expected credit losses that result from all possible default events over the expected life of a financial instrument. [Refer: Type of measurement of expected credit losses [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MethodOfAssessmentOfExpectedCreditLossesAxis' ,N'Method of assessment of expected credit losses [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MethodOfAssessmentOfExpectedCreditLossesMember' ,N'Method of assessment of expected credit losses [member]', N'This member stands for all methods of assessment of expected credit losses. Expected credit losses are the weighted average of credit losses with the respective risks of a default occurring as the weights. This member also represents the standard value for the ''Method of assessment of expected credit losses'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpectedCreditLossesIndividuallyAssessedMember' ,N'Expected credit losses individually assessed [member]', N'This member stands for expected credit losses that are individually assessed. [Refer: Method of assessment of expected credit losses [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpectedCreditLossesCollectivelyAssessedMember' ,N'Expected credit losses collectively assessed [member]', N'This member stands for expected credit losses that are collectively assessed. [Refer: Method of assessment of expected credit losses [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CreditImpairmentOfFinancialInstrumentsAxis' ,N'Credit impairment of financial instruments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CreditImpairmentOfFinancialInstrumentsMember' ,N'Credit impairment of financial instruments [member]', N'This member stands for all statuses of credit impairment of financial instruments. A financial instrument is credit-impaired when one or more events that have a detrimental impact on the estimated future cash flows of that financial instrument have occurred. This member also represents the standard value for the ''Credit impairment of financial instruments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialInstrumentsNotCreditimpairedMember' ,N'Financial instruments not credit-impaired [member]', N'This member stands for financial instruments that are not credit-impaired. [Refer: Credit impairment of financial instruments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialInstrumentsCreditimpairedMember' ,N'Financial instruments credit-impaired [member]', N'This member stands for financial instruments that are credit-impaired. [Refer: Credit impairment of financial instruments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialInstrumentsPurchasedOrOriginatedCreditimpairedMember' ,N'Financial instruments purchased or originated credit-impaired [member]', N'This member stands for financial instruments that were purchased or originated as credit-impaired. [Refer: Financial instruments credit-impaired [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialInstrumentsCreditimpairedAfterPurchaseOrOriginationMember' ,N'Financial instruments credit-impaired after purchase or origination [member]', N'This member stands for financial instruments that were credit-impaired after purchase or origination. [Refer: Financial instruments credit-impaired [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CarryingAmountAccumulatedDepreciationAmortisationAndImpairmentAndGrossCarryingAmountAxis' ,N'Carrying amount, accumulated depreciation, amortisation and impairment and gross carrying amount [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CarryingAmountMember' ,N'Carrying amount [member]', N'This member stands for the amount at which an asset is recognised in the statement of financial position (after deducting any accumulated depreciation or amortisation and accumulated impairment losses). It also represents the standard value for the ''Carrying amount, accumulated depreciation, amortisation and impairment and gross carrying amount'' axis if no other member is used. [Refer: Depreciation and amortisation expense; Impairment loss]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GrossCarryingAmountMember' ,N'Gross carrying amount [member]', N'This member stands for the amount at which an asset is recognised before deducting any accumulated depreciation (amortisation) and accumulated impairment losses thereon. [Refer: Depreciation and amortisation expense; Impairment loss]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AccumulatedImpairmentMember' ,N'Accumulated impairment [member]', N'This member stands for accumulated impairment. [Refer: Impairment loss]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfChangesInLossAllowanceAndExplanationOfChangesInGrossCarryingAmountForFinancialInstrumentsLineItems' ,N'Disclosure of reconciliation of changes in loss allowance and explanation of changes in gross carrying amount for financial instruments [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssets' ,N'Financial assets', N'The amount of assets that are: (a) cash; (b) an equity instrument of another entity; (c) a contractual right: (i) to receive cash or another financial asset from another entity; or (ii) to exchange financial assets or financial liabilities with another entity under conditions that are potentially favourable to the entity; or (d) a contract that will, or may be, settled in the entity’s own equity instruments and is: (i) a non-derivative for which the entity is, or may be, obliged to receive a variable number of the entity’s own equity instruments; or (ii) a derivative that will, or may be, settled other than by the exchange of a fixed amount of cash or another financial asset for a fixed number of the entity’s own equity instruments. For this purpose the entity’s own equity instruments do not include puttable financial instruments classified as equity instruments in accordance with paragraphs 16A-16B of IAS 32, instruments that impose on the entity an obligation to deliver to another party a pro rata share of the net assets of the entity only on liquidation and are classified as equity instruments in accordance with paragraphs 16C-16D of IAS 32, or instruments that are contracts for the future receipt or delivery of the entity’s own equity instruments. [Refer: Financial instruments, class [member]; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughTransfersFinancialAssets' ,N'Increase (decrease) through transfers, financial assets', N'The increase (decrease) in financial assets resulting from transfers. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DecreaseThroughDerecognitionFinancialAssets' ,N'Decrease through derecognition, financial assets', N'The decrease in financial assets resulting from derecognition. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseThroughOriginationOrPurchaseFinancialAssets' ,N'Increase through origination or purchase, financial assets', N'The increase in financial assets resulting from origination or purchase. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DecreaseThroughWriteoffFinancialAssets' ,N'Decrease through write-off, financial assets', N'The decrease in financial assets resulting from write-off. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughChangesInModelsOrRiskParametersFinancialAssets' ,N'Increase (decrease) through changes in models or risk parameters, financial assets', N'The increase (decrease) in financial assets resulting from changes in models or risk parameters. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughModificationOfContractualCashFlowsFinancialAssets' ,N'Increase (decrease) through modification of contractual cash flows, financial assets', N'The increase (decrease) in financial assets resulting from the modification of contractual cash flows. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughForeignExchangeFinancialAssets' ,N'Increase (decrease) through foreign exchange, financial assets', N'The increase (decrease) in financial assets resulting from foreign exchange. [Refer: Financial assets]');
