	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseInFinancialAssets' ,N'Increase (decrease) in financial assets', N'The increase (decrease) in financial assets. [Refer: Financial assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssets' ,N'Financial assets', N'The amount of assets that are: (a) cash; (b) an equity instrument of another entity; (c) a contractual right: (i) to receive cash or another financial asset from another entity; or (ii) to exchange financial assets or financial liabilities with another entity under conditions that are potentially favourable to the entity; or (d) a contract that will, or may be, settled in the entity’s own equity instruments and is: (i) a non-derivative for which the entity is, or may be, obliged to receive a variable number of the entity’s own equity instruments; or (ii) a derivative that will, or may be, settled other than by the exchange of a fixed amount of cash or another financial asset for a fixed number of the entity’s own equity instruments. For this purpose the entity’s own equity instruments do not include puttable financial instruments classified as equity instruments in accordance with paragraphs 16A-16B of IAS 32, instruments that impose on the entity an obligation to deliver to another party a pro rata share of the net assets of the entity only on liquidation and are classified as equity instruments in accordance with paragraphs 16C-16D of IAS 32, or instruments that are contracts for the future receipt or delivery of the entity’s own equity instruments. [Refer: Financial instruments, class [member]; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExposureToCreditRiskOnLoanCommitmentsAndFinancialGuaranteeContracts' ,N'Exposure to credit risk on loan commitments and financial guarantee contracts', N'The amount of the exposure to credit risk on loan commitments and financial guarantee contracts. [Refer: Loan commitments [member]; Financial guarantee contracts [member]; Credit risk [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughTransfersExposureToCreditRiskOnLoanCommitmentsAndFinancialGuaranteeContracts' ,N'Increase (decrease) through transfers, exposure to credit risk on loan commitments and financial guarantee contracts', N'The increase (decrease) in exposure to credit risk on loan commitments and financial guarantee contracts resulting from transfers. [Refer: Exposure to credit risk on loan commitments and financial guarantee contracts]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DecreaseThroughDerecognitionExposureToCreditRiskOnLoanCommitmentsAndFinancialGuaranteeContracts' ,N'Decrease through derecognition, exposure to credit risk on loan commitments and financial guarantee contracts', N'The decrease in exposure to credit risk on loan commitments and financial guarantee contracts resulting from derecognition. [Refer: Exposure to credit risk on loan commitments and financial guarantee contracts]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseThroughOriginationOrPurchaseExposureToCreditRiskOnLoanCommitmentsAndFinancialGuaranteeContracts' ,N'Increase through origination or purchase, exposure to credit risk on loan commitments and financial guarantee contracts', N'The increase in exposure to credit risk on loan commitments and financial guarantee contracts resulting from origination or purchase. [Refer: Exposure to credit risk on loan commitments and financial guarantee contracts]');
