	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TradingSecuritiesMember' ,N'Trading securities [member]', N'This member stands for financial instruments that (a) are acquired or incurred principally for the purpose of selling or repurchasing it in the near term; or (b) on initial recognition are part of a portfolio of identified financial instruments that are managed together and for which there is evidence of a recent actual pattern of short-term profit-taking. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DerivativesMember' ,N'Derivatives [member]', N'This member stands for financial instruments or other contracts within the scope of Ifrs 9 with all three of the following characteristics: (a) Their value changes in response to the change in a specified interest rate, financial instrument price, commodity price, foreign exchange rate, index of prices or rates, credit rating or credit index, or other variable, provided in the case of a non-financial variable that the variable is not specific to a party to the contract (sometimes called the ‘underlying’); (b) They require no initial net investment or an initial net investment that is smaller than would be required for other types of contracts that would be expected to have a similar response to changes in market factors; (c) They are settled at a future date. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OptionContractMember' ,N'Option contract [member]', N'This member stands for a derivative financial instrument that gives the holder the right, but not the obligation, to purchase or sell an underlying asset for a specified price determined in advance. [Refer: Derivatives [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FuturesContractMember' ,N'Futures contract [member]', N'This member stands for a standardised, exchange-traded contract for the purchase or sale of an underlying asset at a specified future date for a settlement price determined in advance.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SwapContractMember' ,N'Swap contract [member]', N'This member stands for a derivative financial instrument that involves the exchange of cash flow streams between the parties of the contract over a specified period. [Refer: Derivatives [member]]');
