	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TradingEquitySecuritiesMember' ,N'Trading equity securities [member]', N'This member stands for equity instruments that (a) are acquired or incurred principally for the purpose of selling or repurchasing it in the near term; or (b) on initial recognition are part of a portfolio of identified financial instruments that are managed together and for which there is evidence of a recent actual pattern of short-term profit-taking.');
