	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Level3OfFairValueHierarchyMember' ,N'Level 3 of fair value hierarchy [member]', N'This member stands for a category of the fair value hierarchy where inputs to the valuation techniques are unobservable inputs for the asset or liability. Unobservable inputs are inputs for which market data are not available and that are developed using the best information available about the assumptions that the market participants would use when pricing the asset or liability.');
