	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MeasurementAxis' ,N'Measurement [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AggregatedMeasurementMember' ,N'Aggregated measurement [member]', N'This member stands for all types of measurement. It also represents the standard value for the ''Measurement'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AtFairValueMember' ,N'At fair value [member]', N'This member stands for measurement based on fair value. Fair value is the price that would be received to sell an asset or paid to transfer a liability in an orderly transaction between market participants at the measurement date.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RecurringFairValueMeasurementMember' ,N'Recurring fair value measurement [member]', N'This member stands for fair value measurements which other Ifrss require or permit in the statement of financial position at the end of each reporting period. [Refer: Ifrss [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NonrecurringFairValueMeasurementMember' ,N'Non-recurring fair value measurement [member]', N'This member stands for fair value measurements which other Ifrss require or permit in the statement of financial position in particular circumstances. [Refer: Ifrss [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NotMeasuredAtFairValueInStatementOfFinancialPositionButForWhichFairValueIsDisclosedMember' ,N'Not measured at fair value in statement of financial position but for which fair value is disclosed [member]', N'This member stands for items not measured at fair value in the statement of financial position but for which fair value is disclosed. [Refer: At fair value [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ClassesOfLiabilitiesAxis' ,N'Classes of liabilities [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiabilitiesMember' ,N'Liabilities [member]', N'Expiry date 2020-01-01: This member stands for present obligations of the entity arising from past events, the settlement of which is expected to result in an outflow from the entity of resources embodying economic benefits. It also represents the standard value for the ''Classes of liabilities'' axis if no other member is used. [Refer: Liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DerivativesMember' ,N'Derivatives [member]', N'This member stands for financial instruments or other contracts within the scope of Ifrs 9 with all three of the following characteristics: (a) Their value changes in response to the change in a specified interest rate, financial instrument price, commodity price, foreign exchange rate, index of prices or rates, credit rating or credit index, or other variable, provided in the case of a non-financial variable that the variable is not specific to a party to the contract (sometimes called the ‘underlying’); (b) They require no initial net investment or an initial net investment that is smaller than would be required for other types of contracts that would be expected to have a similar response to changes in market factors; (c) They are settled at a future date. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContingentConsiderationMember' ,N'Contingent consideration [member]', N'This member stands for an obligation of the acquirer to transfer additional assets or equity interests to the former owners of an acquiree as part of the exchange for control of the acquiree if specified future events occur or conditions are met.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LevelsOfFairValueHierarchyAxis' ,N'Levels of fair value hierarchy [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AllLevelsOfFairValueHierarchyMember' ,N'All levels of fair value hierarchy [member]', N'This member stands for all levels of the fair value hierarchy. It also represents the standard value for the ''Levels of fair value hierarchy'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Level1OfFairValueHierarchyMember' ,N'Level 1 of fair value hierarchy [member]', N'This member stands for a category of the fair value hierarchy where inputs to the valuation techniques are quoted prices (unadjusted) in active markets for identical assets or liabilities that the entity can access at the measurement date.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Level2OfFairValueHierarchyMember' ,N'Level 2 of fair value hierarchy [member]', N'This member stands for a category of the fair value hierarchy where inputs to the valuation techniques are inputs other than quoted prices included within Level 1 that are observable for the asset or liability, either directly or indirectly.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Level3OfFairValueHierarchyMember' ,N'Level 3 of fair value hierarchy [member]', N'This member stands for a category of the fair value hierarchy where inputs to the valuation techniques are unobservable inputs for the asset or liability. Unobservable inputs are inputs for which market data are not available and that are developed using the best information available about the assumptions that the market participants would use when pricing the asset or liability.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfFairValueMeasurementOfLiabilitiesLineItems' ,N'Disclosure of fair value measurement of liabilities [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
