	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MarketApproachMember' ,N'Market approach [member]', N'This member stands for a valuation technique that uses prices and other relevant information generated by market transactions involving identical or comparable (ie similar) assets, liabilities or a group of assets and liabilities, such as a business.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MarketComparableCompaniesMember' ,N'Market comparable companies [member]', N'This member stands for a specific valuation technique consistent with the market approach that involves analysing inputs from comparable entities (for example, EBITDA multiple, revenue multiple, discount for lack of marketability, control premium) in the current market. [Refer: Market approach [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MarketComparablePricesMember' ,N'Market comparable prices [member]', N'This member stands for a specific valuation technique consistent with the market approach that involves analysing inputs from comparable prices (for example, price per square metre) in the current market. [Refer: Market approach [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MatrixPricingMember' ,N'Matrix pricing [member]', N'This member stands for a specific mathematical valuation technique that is consistent with the market approach that is used principally to value some types of financial instruments, such as debt instruments, without relying exclusively on quoted prices for the specific securities, but rather relying on the securities'' relationship to other benchmark quoted securities. [Refer: Market approach [member]; Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ConsensusPricingMember' ,N'Consensus pricing [member]', N'This member stands for a specific valuation technique consistent with the market approach that involves analysing inputs from consensus prices (for example, offered quotes, comparability adjustments) in the market. [Refer: Market approach [member]]');
