	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OptionPricingModelMember' ,N'Option pricing model [member]', N'This member stands for a specific valuation technique consistent with the income approach that involves analysing future amounts with option pricing models, such as the Black-Scholes-Merton formula or a binominal model (ie a lattice model), that incorporate present value techniques and reflect both the time value and intrinsic value of an option. [Refer: Income approach [member]]');
