	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfSignificantRestrictionsOnEntitysAbilityToAccessOrUseAssetsAndSettleLiabilitiesOfGroup' ,N'Description of significant restrictions on entity''s ability to access or use assets and settle liabilities of group', N'The description of significant restrictions (for example, statutory, contractual and regulatory restrictions) on the entity''s ability to access or use the assets and settle the liabilities of the group, such as (a) those that restrict the ability of a parent or its subsidiaries to transfer cash or other assets to (or from) other entities within the group; and (b) guarantees or other requirements that may restrict dividends and other capital distributions being paid, or loans and advances being made or repaid, to (or from) other entities within the group. [Refer: Subsidiaries [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureAndExtentToWhichProtectiveRightsOfNoncontrollingInterestsCanSignificantlyRestrictEntitysAbilityToAccessOrUseAssetsAndSettleLiabilitiesOfGroup' ,N'Description of nature and extent to which protective rights of non-controlling interests can significantly restrict entity''s ability to access or use assets and settle liabilities of group', N'The description of the nature and extent to which protective rights of non-controlling interests can significantly restrict the entity''s ability to access or use the assets and settle the liabilities of the group (such as when a parent is obliged to settle the liabilities of a subsidiary before settling its own liabilities, or when approval of non-controlling interests is required either to access the assets or to settle the liabilities of a subsidiary). Protective rights are rights designed to protect the interest of the party holding those rights without giving that party power over the entity to which those rights relate. [Refer: Non-controlling interests; Subsidiaries [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AssetsToWhichSignificantRestrictionsApply' ,N'Assets to which significant restrictions apply', N'The amount in the consolidated financial statements of the assets of the group to which significant restrictions (for example, statutory, contractual and regulatory restrictions) apply on the entity''s ability to access or use the assets.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiabilitiesToWhichSignificantRestrictionsApply' ,N'Liabilities to which significant restrictions apply', N'The amount in the consolidated financial statements of the liabilities of the group to which significant restrictions (for example, statutory, contractual and regulatory restrictions) apply on the entity''s ability to settle the liabilities of the group.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationAboutConsolidatedStructuredEntitiesExplanatory' ,N'Disclosure of information about consolidated structured entities [text block]', N'The disclosure of information about consolidated structured entities. [Refer: Consolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationAboutConsolidatedStructuredEntitiesTable' ,N'Disclosure of information about consolidated structured entities [table]', N'Schedule disclosing information related to consolidated structured entities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ConsolidatedStructuredEntitiesAxis' ,N'Consolidated structured entities [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForConsolidatedStructuredEntitiesMember' ,N'Entity''s total for consolidated structured entities [member]', N'This member stands for the standard value for the ''Consolidated structured entities'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ConsolidatedStructuredEntitiesMember' ,N'Consolidated structured entities [member]', N'This member stands for consolidated structured entities. A structured entity is an entity that has been designed so that voting or similar rights are not the dominant factor in deciding who controls the entity, such as when any voting rights relate to administrative tasks only and the relevant activities are directed by means of contractual arrangements. [Refer: Consolidated [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationAboutConsolidatedStructuredEntitiesLineItems' ,N'Disclosure of information about consolidated structured entities [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTermsOfContractualArrangementsThatCouldRequireParentOrSubsidiariesToProvideFinancialSupportToStructuredEntity' ,N'Description of terms of contractual arrangements that could require parent or subsidiaries to provide financial support to structured entity', N'The description of the terms of contractual arrangements that could require the parent or its subsidiaries to provide financial support to a structured entity, including events or circumstances that could expose the reporting entity to a loss (for example, liquidity arrangements or credit rating triggers associated with obligations to purchase assets of the structured entity or provide financial support). [Refer: Subsidiaries [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTypeOfSupportProvidedToStructuredEntityWithoutHavingContractualObligationToDoSo' ,N'Description of type of support provided to structured entity without having contractual obligation to do so', N'The description of the type of financial or other support (for example, purchasing assets of, or instruments issued by, the structured entity) provided to a structured entity without having a contractual obligation to do so, including situations in which the entity assisted the structured entity in obtaining financial support. [Refer: Subsidiaries [member]; Support provided to structured entity without having contractual obligation to do so]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SupportProvidedToStructuredEntityWithoutHavingContractualObligationToDoSo' ,N'Support provided to structured entity without having contractual obligation to do so', N'The amount of financial or other support (for example, purchasing assets of, or instruments issued by, the structured entity) provided to a structured entity without having a contractual obligation to do so, including assistance in obtaining financial support. [Refer: Subsidiaries [member]; Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonsForProvidingSupportToStructuredEntityWithoutHavingContractualObligationToDoSo' ,N'Description of reasons for providing support to structured entity without having contractual obligation to do so', N'The description of reasons for providing financial or other support (for example, purchasing assets of, or instruments issued by, the structured entity) to a structured entity without having a contractual obligation to do so, including situations in which the entity assisted the structured entity in obtaining financial support.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfFactorsInReachingDecisionThatProvisionOfSupportToPreviouslyUnconsolidatedStructuredEntityResultedInObtainingControl' ,N'Explanation of factors in reaching decision to provide support to previously unconsolidated structured entity that resulted in obtaining control', N'The explanation of the relevant factors in reaching the decision by a parent or any of its subsidiaries to provide, without having a contractual obligation to do so, financial or other support to a previously unconsolidated structured entity that resulted in the entity controlling the structured entity. [Refer: Subsidiaries [member]; Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfIntentionsToProvideSupportToStructuredEntity' ,N'Description of intentions to provide support to structured entity', N'The description of the entity''s current intentions to provide financial or other support to a structured entity, including intentions to assist the structured entity in obtaining financial support.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfEffectsOfChangesInParentsOwnershipInterestInSubsidiaryThatDoNotResultInLossOfControlOnEquityAttributableToOwnersOfParentExplanatory' ,N'Disclosure of effects of changes in parent''s ownership interest in subsidiary that do not result in loss of control on equity attributable to owners of parent [text block]', N'The disclosure of a schedule that shows the effects of any changes in the parent''s ownership interest in a subsidiary that do not result in a loss of control on the equity attributable to owners of the parent.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughChangesInOwnershipInterestsInSubsidiariesThatDoNotResultInLossOfControlEquityAttributableToOwnersOfParent' ,N'Increase (decrease) through changes in ownership interests in subsidiaries that do not result in loss of control, equity attributable to owners of parent', N'The increase (decrease) in equity attributable to owners of the parent through changes in ownership interests in subsidiaries that do not result in loss of control. [Refer: Subsidiaries [member]; Equity attributable to owners of parent]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsLossesRecognisedWhenControlInSubsidiaryIsLost' ,N'Gains (losses) recognised when control of subsidiary is lost', N'The gains (losses) recognised in association with the loss of control over a subsidiary, which are attributable to the former controlling interest.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PortionOfGainsLossesRecognisedWhenControlOfSubsidiaryIsLostAttributableToRecognisingInvestmentRetainedInFormerSubsidiary' ,N'Portion of gains (losses) recognised when control of subsidiary is lost, attributable to recognising investment retained in former subsidiary', N'The portion of gains (losses) from losing control of a subsidiary attributable to recognising any investment retained in the former subsidiary at its fair value at the date when control is lost. [Refer: Gains (losses) recognised when control of subsidiary is lost; Subsidiaries [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfGainsLossesRecognisedWhenControlInSubsidiaryIsLost' ,N'Description of line item(s) in profit or loss in which gain (loss) is recognised when control of subsidiary is lost', N'The description of the line item(s) in profit or loss in which the gain (loss) is recognised (if not presented separately) when control of a subsidiary is lost. [Refer: Subsidiaries [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInterestsInAssociatesExplanatory' ,N'Disclosure of interests in associates [text block]', N'The disclosure of interests in associates. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfSignificantInvestmentsInAssociatesExplanatory' ,N'Disclosure of associates [text block]', N'The disclosure of associates. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfSignificantInvestmentsInAssociatesTable' ,N'Disclosure of associates [table]', N'Schedule disclosing information related to associates.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SignificantInvestmentsInAssociatesAxis' ,N'Associates [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForAssociatesMember' ,N'Entity''s total for associates [member]', N'This member stands for the standard value for the ''Associates'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AssociatesMember' ,N'Associates [member]', N'This member stands for the entities over which the investor has significant influence.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AggregatedIndividuallyImmaterialAssociatesMember' ,N'Aggregated individually immaterial associates [member]', N'This member stands for the aggregation of associates that are individually immaterial. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfSignificantInvestmentsInAssociatesLineItems' ,N'Disclosure of associates [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NameOfAssociate' ,N'Name of associate', N'The name of an associate. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfEntitysRelationshipWithAssociate' ,N'Description of nature of entity''s relationship with associate', N'The description of the nature of the entity''s relationship with an associate (for example, describing the nature of the activities of the associate and whether they are strategic to the entity''s activities). [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PrincipalPlaceOfBusinessOfAssociate' ,N'Principal place of business of associate', N'The principal place of business of an associate. [Refer: Principal place of business; Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CountryOfIncorporationOrResidenceOfAssociate' ,N'Country of incorporation of associate', N'The country in which an associate of the entity is incorporated. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProportionOfOwnershipInterestInAssociate' ,N'Proportion of ownership interest in associate', N'The proportion of ownership interest in an associate attributable to the entity. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProportionOfVotingPowerHeldInAssociate' ,N'Proportion of voting rights held in associate', N'The proportion of the voting rights in an associate held by the entity. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfWhetherInvestmentInAssociateIsMeasuredUsingEquityMethodOrAtFairValue' ,N'Description of whether investment in associate is measured using equity method or at fair value', N'The description of whether the investment in an associate is measured using the equity method or at fair value. [Refer: At fair value [member]; Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOfPreparationOfSummarisedFinancialInformationOfAssociate' ,N'Description of basis of preparation of summarised financial information of associate', N'The description of the basis of preparation of summarised financial information of an associate. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DividendsReceived' ,N'Dividends received', N'The amount of dividends received.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrentAssets' ,N'Current assets', N'The amount of assets that the entity (a) expects to realise or intends to sell or consume in its normal operating cycle; (b) holds primarily for the purpose of trading; (c) expects to realise within twelve months after the reporting period; or (d) classifies as cash or cash equivalents (as defined in IAS 7) unless the asset is restricted from being exchanged or used to settle a liability for at least twelve months after the reporting period. [Refer: Assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NoncurrentAssets' ,N'Non-current assets', N'The amount of assets that do not meet the definition of current assets. [Refer: Current assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrentLiabilities' ,N'Current liabilities', N'The amount of liabilities that: (a) the entity expects to settle in its normal operating cycle; (b) the entity holds primarily for the purpose of trading; (c) are due to be settled within twelve months after the reporting period; or (d) the entity does not have an unconditional right to defer settlement for at least twelve months after the reporting period.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NoncurrentLiabilities' ,N'Non-current liabilities', N'The amount of liabilities that do not meet the definition of current liabilities. [Refer: Current liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Revenue' ,N'Revenue', N'Expiry date 2020-01-01: The income arising in the course of an entity''s ordinary activities. Income is increases in economic benefits during the accounting period in the form of inflows or enhancements of assets or decreases of liabilities that result in an increase in equity, other than those relating to contributions from equity participants.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLossFromContinuingOperations' ,N'Profit (loss) from continuing operations', N'The profit (loss) from continuing operations. [Refer: Continuing operations [member]; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLossFromDiscontinuedOperations' ,N'Profit (loss) from discontinued operations', N'The profit (loss) from discontinued operations. [Refer: Discontinued operations [member]; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherComprehensiveIncome' ,N'Other comprehensive income', N'The amount of income and expense (including reclassification adjustments) that is not recognised in profit or loss as required or permitted by Ifrss. [Refer: Ifrss [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ComprehensiveIncome' ,N'Comprehensive income', N'The amount of change in equity resulting from transactions and other events, other than those changes resulting from transactions with owners in their capacity as owners.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FairValueOfInvestmentsInAssociatesWherePriceQuotationsPublished' ,N'Fair value of investments in associates for which there are quoted market prices', N'The fair value of investments in associates if there are quoted market prices for the investment. [Refer: Associates [member]; Investments in associates]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InvestmentAccountedForUsingEquityMethod' ,N'Investments accounted for using equity method', N'The amount of investments accounted for using the equity method. The equity method is a method of accounting whereby the investment is initially recognised at cost and adjusted thereafter for the post-acquisition change in the investor''s share of net assets of the investee. The investor''s profit or loss includes its share of the profit or loss of the investee. The investor''s other comprehensive income includes its share of the other comprehensive income of the investee. [Refer: At cost [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfProfitLossOfContinuingOperationsOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of profit (loss) from continuing operations of associates and joint ventures accounted for using equity method', N'The entity''s share of the profit (loss) from continuing operations of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Continuing operations [member]; Investments accounted for using equity method; Joint ventures [member]; Profit (loss) from continuing operations]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfProfitLossOfDiscontinuedOperationsOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of post-tax profit (loss) from discontinued operations of associates and joint ventures accounted for using equity method', N'The entity''s share of the post-tax profit (loss) from discontinued operations of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Discontinued operations [member]; Investments accounted for using equity method; Joint ventures [member]; Profit (loss) from discontinued operations]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfOtherComprehensiveIncomeOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of other comprehensive income of associates and joint ventures accounted for using equity method, net of tax', N'The entity''s share of the other comprehensive income of associates and joint ventures accounted for using the equity method, net of tax. [Refer: Associates [member]; Investments accounted for using equity method; Joint ventures [member]; Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfTotalComprehensiveIncomeOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of total comprehensive income of associates and joint ventures accounted for using equity method', N'The entity''s share of the total comprehensive income of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Joint ventures [member]; Investments accounted for using equity method]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfSummarisedFinancialInformationOfAssociateAccountedForUsingEquityMethodToCarryingAmountOfInterestInAssociateExplanatory' ,N'Disclosure of reconciliation of summarised financial information of associate accounted for using equity method to carrying amount of interest in associate [text block]', N'The disclosure of the reconciliation of the summarised financial information of an associate accounted for using the equity method to the carrying amount of the reporting entity''s interest in the associate. [Refer: Carrying amount [member]; Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureAndExtentOfSignificantRestrictionsOnTransferOfFundsToParent' ,N'Description of nature and extent of significant restrictions on transfer of funds to entity', N'The description of the nature and extent of any significant restrictions (for example, resulting from borrowing arrangements or regulatory requirements) on the ability of other entities to transfer funds to the reporting entity in the form of cash dividends or to repay loans or advances.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DateOfEndOfReportingPeriodOfFinancialStatementsOfAssociate' ,N'Date of end of reporting period of financial statements of associate', N'The date of the end of the reporting period of the financial statements of an associate.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonWhyUsingDifferentReportingDateOrPeriodForAssociate' ,N'Description of reason why using different reporting date or period for associate', N'The description of the reason why the financial statements of an associate used in applying the equity method are as of a date or for a period that is different from that of the entity. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnrecognisedShareOfLossesOfAssociates' ,N'Unrecognised share of losses of associates', N'The amount of the unrecognised share of associates'' losses if the entity has stopped recognising its share of losses when applying the equity method. [Refer: Associates [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CumulativeUnrecognisedShareOfLossesOfAssociates' ,N'Cumulative unrecognised share of losses of associates', N'The cumulative amount of the unrecognised share of losses of associates if the entity has stopped recognising its share of losses when applying the equity method. [Refer: Associates [member]; Unrecognised share of losses of associates]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContingentLiabilitiesIncurredInRelationToInterestsInAssociates' ,N'Contingent liabilities incurred in relation to interests in associates', N'The amount of contingent liabilities incurred relating to the entity''s interests in associates. [Refer: Associates [member]; Contingent liabilities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfContingentLiabilitiesOfAssociatesIncurredJointlyWithOtherInvestors' ,N'Share of contingent liabilities of associates incurred jointly with other investors', N'The entity''s share of contingent liabilities incurred jointly with other investors with significant influence over associates. [Refer: Associates [member]; Contingent liabilities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInterestsInJointArrangementsExplanatory' ,N'Disclosure of interests in joint arrangements [text block]', N'The disclosure of interests in joint arrangements. A joint arrangement is an arrangement of which two or more parties have joint control.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfJointOperationsExplanatory' ,N'Disclosure of joint operations [text block]', N'The disclosure of joint operations. [Refer: Joint operations [member]]');
