	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'JointOperationsAxis' ,N'Joint operations [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForJointOperationsMember' ,N'Entity''s total for joint operations [member]', N'This member stands for the standard value for the ''Joint operations'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'JointOperationsMember' ,N'Joint operations [member]', N'This member stands for joint arrangements whereby the parties that have joint control of the arrangement have rights to the assets, and obligations for the liabilities, relating to the arrangement.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfJointOperationsLineItems' ,N'Disclosure of joint operations [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NameOfJointOperation' ,N'Name of joint operation', N'The name of a joint operation. [Refer: Joint operations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfEntitysRelationshipWithJointOperation' ,N'Description of nature of entity''s relationship with joint operation', N'The description of the nature of the entity''s relationship with a joint operation (for example, describing the nature of the activities of the joint operation and whether they are strategic to the entity''s activities). [Refer: Joint operations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PrincipalPlaceOfBusinessOfJointOperation' ,N'Principal place of business of joint operation', N'The principal place of business of a joint operation. [Refer: Joint operations [member]; Principal place of business]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CountryOfIncorporationOfJointOperation' ,N'Country of incorporation of joint operation', N'The country in which a joint operation of the entity is incorporated. [Refer: Joint operations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProportionOfOwnershipInterestInJointOperation' ,N'Proportion of ownership interest in joint operation', N'The proportion of ownership interest in a joint operation attributable to the entity. [Refer: Joint operations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProportionOfVotingRightsHeldInJointOperation' ,N'Proportion of voting rights held in joint operation', N'The proportion of the voting rights in a joint operation held by the entity. [Refer: Joint operations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfJointVenturesExplanatory' ,N'Disclosure of joint ventures [text block]', N'The disclosure of joint ventures. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfJointVenturesTable' ,N'Disclosure of joint ventures [table]', N'Schedule disclosing information related to joint ventures.');
