	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForJointVenturesMember' ,N'Entity''s total for joint ventures [member]', N'This member stands for the standard value for the ''Joint ventures'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'JointVenturesMember' ,N'Joint ventures [member]', N'This member stands for joint arrangements whereby the parties that have joint control of the arrangement have rights to the net assets of the arrangement.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AggregatedIndividuallyImmaterialJointVenturesMember' ,N'Aggregated individually immaterial joint ventures [member]', N'This member stands for the aggregation of joint ventures that are individually immaterial. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfJointVenturesLineItems' ,N'Disclosure of joint ventures [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NameOfJointVenture' ,N'Name of joint venture', N'The name of a joint venture. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfEntitysRelationshipWithJointVenture' ,N'Description of nature of entity''s relationship with joint venture', N'The description of the nature of the entity''s relationship with a joint venture (for example, describing the nature of the activities of the joint venture and whether they are strategic to the entity''s activities). [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PrincipalPlaceOfBusinessOfJointVenture' ,N'Principal place of business of joint venture', N'The principal place of business of a joint venture. [Refer: Principal place of business; Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CountryOfIncorporationOfJointVenture' ,N'Country of incorporation of joint venture', N'The country in which a joint venture of the entity is incorporated. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProportionOfOwnershipInterestInJointVenture' ,N'Proportion of ownership interest in joint venture', N'The proportion of ownership interest in a joint venture attributable to the entity. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProportionOfVotingRightsHeldInJointVenture' ,N'Proportion of voting rights held in joint venture', N'The proportion of the voting rights in a joint venture held by the entity. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfWhetherInvestmentInJointVentureIsMeasuredUsingEquityMethodOrAtFairValue' ,N'Description of whether investment in joint venture is measured using equity method or at fair value', N'The description of whether the investment in a joint venture is measured using the equity method or at fair value. [Refer: At fair value [member]; Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOfPreparationOfSummarisedFinancialInformationOfJointVenture' ,N'Description of basis of preparation of summarised financial information of joint venture', N'The description of the basis of preparation of summarised financial information of a joint venture. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DividendsReceived' ,N'Dividends received', N'The amount of dividends received.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrentAssets' ,N'Current assets', N'The amount of assets that the entity (a) expects to realise or intends to sell or consume in its normal operating cycle; (b) holds primarily for the purpose of trading; (c) expects to realise within twelve months after the reporting period; or (d) classifies as cash or cash equivalents (as defined in IAS 7) unless the asset is restricted from being exchanged or used to settle a liability for at least twelve months after the reporting period. [Refer: Assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NoncurrentAssets' ,N'Non-current assets', N'The amount of assets that do not meet the definition of current assets. [Refer: Current assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrentLiabilities' ,N'Current liabilities', N'The amount of liabilities that: (a) the entity expects to settle in its normal operating cycle; (b) the entity holds primarily for the purpose of trading; (c) are due to be settled within twelve months after the reporting period; or (d) the entity does not have an unconditional right to defer settlement for at least twelve months after the reporting period.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NoncurrentLiabilities' ,N'Non-current liabilities', N'The amount of liabilities that do not meet the definition of current liabilities. [Refer: Current liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Revenue' ,N'Revenue', N'Expiry date 2020-01-01: The income arising in the course of an entity''s ordinary activities. Income is increases in economic benefits during the accounting period in the form of inflows or enhancements of assets or decreases of liabilities that result in an increase in equity, other than those relating to contributions from equity participants.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLossFromContinuingOperations' ,N'Profit (loss) from continuing operations', N'The profit (loss) from continuing operations. [Refer: Continuing operations [member]; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLossFromDiscontinuedOperations' ,N'Profit (loss) from discontinued operations', N'The profit (loss) from discontinued operations. [Refer: Discontinued operations [member]; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherComprehensiveIncome' ,N'Other comprehensive income', N'The amount of income and expense (including reclassification adjustments) that is not recognised in profit or loss as required or permitted by Ifrss. [Refer: Ifrss [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ComprehensiveIncome' ,N'Comprehensive income', N'The amount of change in equity resulting from transactions and other events, other than those changes resulting from transactions with owners in their capacity as owners.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CashAndCashEquivalents' ,N'Cash and cash equivalents', N'The amount of cash on hand and demand deposits, along with short-term, highly liquid investments that are readily convertible to known amounts of cash and that are subject to an insignificant risk of changes in value. [Refer: Cash; Cash equivalents]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherCurrentFinancialLiabilities' ,N'Other current financial liabilities', N'The amount of current financial liabilities that the entity does not separately disclose in the same statement or note. [Refer: Other financial liabilities; Current financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherNoncurrentFinancialLiabilities' ,N'Other non-current financial liabilities', N'The amount of non-current financial liabilities that the entity does not separately disclose in the same statement or note. [Refer: Other financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DepreciationAndAmortisationExpense' ,N'Depreciation and amortisation expense', N'The amount of depreciation and amortisation expense. Depreciation and amortisation are the systematic allocations of depreciable amounts of assets over their useful lives.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RevenueFromInterest' ,N'Interest income', N'The amount of income arising from interest.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InterestExpense' ,N'Interest expense', N'The amount of expense arising from interest.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeTaxExpenseContinuingOperations' ,N'Tax expense (income)', N'The aggregate amount included in the determination of profit (loss) for the period in respect of current tax and deferred tax. [Refer: Current tax expense (income); Deferred tax expense (income)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FairValueOfInvestmentInJointVenturesWherePriceQuotationsPublished' ,N'Fair value of investments in joint ventures for which there are quoted market prices', N'The fair value of investments in joint ventures if there are quoted market prices for the investment. [Refer: Joint ventures [member]; Investments in joint ventures]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InvestmentAccountedForUsingEquityMethod' ,N'Investments accounted for using equity method', N'The amount of investments accounted for using the equity method. The equity method is a method of accounting whereby the investment is initially recognised at cost and adjusted thereafter for the post-acquisition change in the investor''s share of net assets of the investee. The investor''s profit or loss includes its share of the profit or loss of the investee. The investor''s other comprehensive income includes its share of the other comprehensive income of the investee. [Refer: At cost [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfProfitLossOfContinuingOperationsOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of profit (loss) from continuing operations of associates and joint ventures accounted for using equity method', N'The entity''s share of the profit (loss) from continuing operations of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Continuing operations [member]; Investments accounted for using equity method; Joint ventures [member]; Profit (loss) from continuing operations]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfProfitLossOfDiscontinuedOperationsOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of post-tax profit (loss) from discontinued operations of associates and joint ventures accounted for using equity method', N'The entity''s share of the post-tax profit (loss) from discontinued operations of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Discontinued operations [member]; Investments accounted for using equity method; Joint ventures [member]; Profit (loss) from discontinued operations]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfOtherComprehensiveIncomeOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of other comprehensive income of associates and joint ventures accounted for using equity method, net of tax', N'The entity''s share of the other comprehensive income of associates and joint ventures accounted for using the equity method, net of tax. [Refer: Associates [member]; Investments accounted for using equity method; Joint ventures [member]; Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfTotalComprehensiveIncomeOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of total comprehensive income of associates and joint ventures accounted for using equity method', N'The entity''s share of the total comprehensive income of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Joint ventures [member]; Investments accounted for using equity method]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfSummarisedFinancialInformationOfJointVentureAccountedForUsingEquityMethodToCarryingAmountOfInterestInJointVentureExplanatory' ,N'Disclosure of reconciliation of summarised financial information of joint venture accounted for using equity method to carrying amount of interest in joint venture [text block]', N'The disclosure of the reconciliation of the summarised financial information of a joint venture accounted for using the equity method to the carrying amount of the reporting entity''s interest in the joint venture. [Refer: Carrying amount [member]; Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureAndExtentOfSignificantRestrictionsOnTransferOfFundsToParent' ,N'Description of nature and extent of significant restrictions on transfer of funds to entity', N'The description of the nature and extent of any significant restrictions (for example, resulting from borrowing arrangements or regulatory requirements) on the ability of other entities to transfer funds to the reporting entity in the form of cash dividends or to repay loans or advances.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DateOfEndOfReportingPeriodOfFinancialStatementsOfJointVenture2013' ,N'Date of end of reporting period of financial statements of joint venture', N'The date of the end of the reporting period of the financial statements of a joint venture.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonWhyUsingDifferentReportingDateOrPeriodForJointVenture' ,N'Description of reason why using different reporting date or period for joint venture', N'The description of the reason why the financial statements of a joint venture used in applying the equity method are as of a date or for a period that is different from that of the entity. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnrecognisedShareOfLossesOfJointVentures' ,N'Unrecognised share of losses of joint ventures', N'The amount of the unrecognised share of joint ventures'' losses if the entity has stopped recognising its share of losses when applying the equity method. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CumulativeUnrecognisedShareOfLossesOfJointVentures' ,N'Cumulative unrecognised share of losses of joint ventures', N'The cumulative amount of the unrecognised share of losses of joint ventures if the entity has stopped recognising its share of losses when applying the equity method. [Refer: Joint ventures [member]; Unrecognised share of losses of joint ventures]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CommitmentsInRelationToJointVentures' ,N'Commitments in relation to joint ventures', N'The commitments that the entity has relating to its joint ventures as specified in paragraphs B18-B20 of Ifrs 12. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContingentLiabilitiesIncurredByVenturerInRelationToInterestsInJointVentures' ,N'Contingent liabilities incurred in relation to interests in joint ventures', N'The amount of contingent liabilities incurred in relation to interests in joint ventures. [Refer: Contingent liabilities [member]; Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfContingentLiabilitiesIncurredJointlyWithOtherVenturers' ,N'Share of contingent liabilities of joint ventures incurred jointly with other investors', N'The entity''s share of contingent liabilities incurred jointly with other investors with joint control of the joint ventures. [Refer: Contingent liabilities [member]; Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInterestsInUnconsolidatedStructuredEntitiesExplanatory' ,N'Disclosure of interests in unconsolidated structured entities [text block]', N'The disclosure of interests in structured entities that are not controlled by the entity (unconsolidated structured entities). [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfUnconsolidatedStructuredEntitiesExplanatory' ,N'Disclosure of unconsolidated structured entities [text block]', N'The disclosure of unconsolidated structured entities. [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfUnconsolidatedStructuredEntitiesTable' ,N'Disclosure of unconsolidated structured entities [table]', N'Schedule disclosing information related to unconsolidated structured entities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnconsolidatedStructuredEntitiesAxis' ,N'Unconsolidated structured entities [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForUnconsolidatedStructuredEntitiesMember' ,N'Entity''s total for unconsolidated structured entities [member]', N'This member stands for the standard value for the ''Unconsolidated structured entities'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnconsolidatedStructuredEntitiesMember' ,N'Unconsolidated structured entities [member]', N'This member stands for unconsolidated structured entities. A structured entity is an entity that has been designed so that voting or similar rights are not the dominant factor in deciding who controls the entity, such as when any voting rights relate to administrative tasks only and the relevant activities are directed by means of contractual arrangements. [Refer: Consolidated [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SecuritisationVehiclesMember' ,N'Securitisation vehicles [member]', N'This member stands for vehicles used for the process of securitisation, whereby individual assets are pooled together and sold to a special purpose vehicle that issues debt instruments backed by the pool of assets.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AssetbackedFinancingsMember' ,N'Asset-backed financings [member]', N'This member stands for asset-backed financings.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InvestmentFundsMember' ,N'Investment funds [member]', N'This member stands for investment funds.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfUnconsolidatedStructuredEntitiesLineItems' ,N'Disclosure of unconsolidated structured entities [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationAboutInterestsInStructuredEntityExplanatory' ,N'Disclosure of information about interests in structured entity [text block]', N'The disclosure of qualitative and quantitative information about the entity''s interests in structured entities, including, but not limited to, the nature, purpose, size and activities of the structured entity and how the structured entity is financed.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfHowEntityDeterminedWhichStructuredEntitiesItSponsored' ,N'Description of how entity determined which structured entities it sponsored', N'The description of how the entity has determined which structured entities it has sponsored.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeFromStructuredEntities' ,N'Income from structured entities', N'The amount of income from structured entities including recurring and non-recurring fees, interest, dividends, gains or losses on the remeasurement or derecognition of interests in structured entities and gains or losses from the transfer of assets and liabilities to the structured entity. [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTypesOfIncomeFromStructuredEntities' ,N'Description of types of income from structured entities', N'The description of the types of income from structured entities. [Refer: Income from structured entities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AssetsTransferredToStructuredEntitiesAtTimeOfTransfer' ,N'Assets transferred to structured entities, at time of transfer', N'The amount, at the time of transfer, of all assets transferred to structured entities. [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AssetsRecognisedInEntitysFinancialStatementsInRelationToStructuredEntities' ,N'Assets recognised in entity''s financial statements in relation to structured entities', N'The amount of assets recognised in the entity''s financial statements relating to its interests in structured entities. [Refer: Assets; Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiabilitiesRecognisedInEntitysFinancialStatementsInRelationToStructuredEntities' ,N'Liabilities recognised in entity''s financial statements in relation to structured entities', N'The amount of liabilities recognised in the entity''s financial statements relating to its interests in structured entities. [Refer: Liabilities; Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfLineItemsInStatementOfFinancialPositionInWhichAssetsAndLiabilitiesRecognisedInRelationToStructuredEntitiesAreRecognised' ,N'Description of line items in statement of financial position in which assets and liabilities recognised in relation to structured entities are recognised', N'The description of the line items in the statement of financial position in which assets and liabilities in relation to structured entities are recognised.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MaximumExposureToLossFromInterestsInStructuredEntities' ,N'Maximum exposure to loss from interests in structured entities', N'The amount that best represents the entity''s maximum exposure to loss from its interests in structured entities. [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutHowMaximumExposureToLossFromInterestsInStructuredEntitiesIsDetermined' ,N'Information about how maximum exposure to loss from interests in structured entities is determined', N'Information about how the amount that best represents the entity''s maximum exposure to loss from its interests in structured entities is determined. [Refer: Maximum exposure to loss from interests in structured entities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfFactAndReasonsWhyMaximumExposureToLossFromInterestsInStructuredEntitiesCannotBeQuantified' ,N'Description of fact and reasons why maximum exposure to loss from interests in structured entities cannot be quantified', N'The description of the fact and reasons why the entity cannot quantify its maximum exposure to loss from its interests in structured entities. [Refer: Maximum exposure to loss from interests in structured entities; Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfComparisonBetweenAssetsAndLiabilitiesRecognisedInRelationToStructuredEntitiesAndMaximumExposureToLossFromInterestsInStructuredEntities' ,N'Description of comparison between assets and liabilities recognised in relation to structured entities and maximum exposure to loss from interests in structured entities', N'The description of the comparison of the carrying amount of assets and liabilities of the entity that relate to its interests in structured entities and the entity''s maximum exposure to loss from those entities. [Refer: Carrying amount [member]; Unconsolidated structured entities [member]; Maximum exposure to loss from interests in structured entities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTypeOfSupportProvidedToStructuredEntityWithoutHavingContractualObligationToDoSo' ,N'Description of type of support provided to structured entity without having contractual obligation to do so', N'The description of the type of financial or other support (for example, purchasing assets of, or instruments issued by, the structured entity) provided to a structured entity without having a contractual obligation to do so, including situations in which the entity assisted the structured entity in obtaining financial support. [Refer: Subsidiaries [member]; Support provided to structured entity without having contractual obligation to do so]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SupportProvidedToStructuredEntityWithoutHavingContractualObligationToDoSo' ,N'Support provided to structured entity without having contractual obligation to do so', N'The amount of financial or other support (for example, purchasing assets of, or instruments issued by, the structured entity) provided to a structured entity without having a contractual obligation to do so, including assistance in obtaining financial support. [Refer: Subsidiaries [member]; Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonsForProvidingSupportToStructuredEntityWithoutHavingContractualObligationToDoSo' ,N'Description of reasons for providing support to structured entity without having contractual obligation to do so', N'The description of reasons for providing financial or other support (for example, purchasing assets of, or instruments issued by, the structured entity) to a structured entity without having a contractual obligation to do so, including situations in which the entity assisted the structured entity in obtaining financial support.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfIntentionsToProvideSupportToStructuredEntity' ,N'Description of intentions to provide support to structured entity', N'The description of the entity''s current intentions to provide financial or other support to a structured entity, including intentions to assist the structured entity in obtaining financial support.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AdditionalInformationAboutNatureOfAndChangesInRisksAssociatedWithInterestsInStructuredEntitiesExplanatory' ,N'Additional information about nature of and changes in risks associated with interests in structured entities [text block]', N'The disclosure of additional information about the nature of, and changes in, risks associated with interests in structured entities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTermsOfContractualArrangementsThatCouldRequireParentOrSubsidiariesToProvideFinancialSupportToStructuredEntity' ,N'Description of terms of contractual arrangements that could require parent or subsidiaries to provide financial support to structured entity', N'The description of the terms of contractual arrangements that could require the parent or its subsidiaries to provide financial support to a structured entity, including events or circumstances that could expose the reporting entity to a loss (for example, liquidity arrangements or credit rating triggers associated with obligations to purchase assets of the structured entity or provide financial support). [Refer: Subsidiaries [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LossesIncurredInRelationToInterestsInStructuredEntities' ,N'Losses incurred in relation to interests in structured entities', N'The amount of losses incurred by the entity relating to its interests in structured entities. [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfWhetherEntityIsRequiredToAbsorbLossesOfStructuredEntitiesBeforeOtherParties' ,N'Description of whether entity is required to absorb losses of structured entities before other parties', N'The description of whether the entity is required to absorb losses of structured entities before other parties.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MaximumLimitOfLossesOfStructuredEntitiesWhichEntityIsRequiredToAbsorbBeforeOtherParties' ,N'Maximum limit of losses of structured entities which entity is required to absorb before other parties', N'The maximum limit of losses of structured entities that the entity is required to absorb before other parties. [Refer: Unconsolidated structured entities [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfRankingAndAmountsOfPotentialLossesInStructuredEntitiesBorneByPartiesWhoseInterestsRankLowerThanEntitysInterestsExplanatory' ,N'Disclosure of ranking and amounts of potential losses in structured entities borne by parties whose interests rank lower than entity''s interests [text block]', N'The disclosure of the ranking and amounts of potential losses in structured entities borne by parties whose interests rank lower than entity''s interests in the structured entities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationAboutLiquidityArrangementsGuaranteesOrOtherCommitmentsWithThirdPartiesThatMayAffectFairValueOrRiskOfInterestsInStructuredEntitiesExplanatory' ,N'Disclosure of information about liquidity arrangements, guarantees or other commitments with third parties that may affect fair value or risk of interests in structured entities [text block]', N'The disclosure of information about liquidity arrangements, guarantees or other commitments with third parties that may affect the fair value or risk of the entity''s interests in structured entities. [Refer: Guarantees [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfDifficultiesStructuredEntityExperiencedInFinancingItsActivities' ,N'Description of difficulties structured entity experienced in financing its activities', N'The description of the difficulties that a structured entity has experienced in financing its activities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfFormsOfFundingOfStructuredEntityAndTheirWeightedaverageLifeExplanatory' ,N'Disclosure of forms of funding of structured entity and their weighted-average life [text block]', N'The disclosure of the forms of funding (for example, commercial paper or medium-term notes) of structured entities and their weighted-average life.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInvestmentEntitiesExplanatory' ,N'Disclosure of investment entities [text block]', N'The disclosure of investment entities. An investment entity is an entity that: (a) obtains funds from one or more investors for the purpose of providing those investor(s) with investment management services; (b) commits to its investor(s) that its business purpose is to invest funds solely for returns from capital appreciation, investment income, or both; and (c) measures and evaluates the performance of substantially all of its investments on a fair value basis.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutSignificantJudgementsAndAssumptionsMadeInDeterminingThatEntityIsInvestmentEntity' ,N'Information about significant judgements and assumptions made in determining that entity is investment entity', N'Information about significant judgements and assumptions made in determining that the entity is an investment entity. [Refer: Disclosure of investment entities [text block]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonsForConcludingThatEntityIsInvestmentEntityIfItDoesNotHaveOneOrMoreTypicalCharacteristics' ,N'Description of reasons for concluding that entity is investment entity if it does not have one or more typical characteristics', N'The description of the reasons for concluding that the entity is an investment entity if it does not have one or more typical characteristics of an investment entity. [Refer: Disclosure of investment entities [text block]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfChangeOfInvestmentEntityStatus' ,N'Description of change of investment entity status', N'The description of the change of investment entity status. [Refer: Disclosure of investment entities [text block]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonsForChangeOfInvestmentEntityStatus' ,N'Description of reasons for change of investment entity status', N'The description of the reasons for the change of investment entity status. [Refer: Disclosure of investment entities [text block]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfEffectOfChangeOfInvestmentEntityStatusOnFinancialStatementsExplanatory' ,N'Disclosure of effect of change of investment entity status on financial statements [text block]', N'The disclosure of the effect of the change of investment entity status on the financial statements. [Refer: Disclosure of investment entities [text block]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FairValueOfSubsidiariesThatCeaseToBeConsolidatedAsOfDateOfChangeOfInvestmentEntityStatus' ,N'Fair value of subsidiaries that cease to be consolidated as of date of change of investment entity status', N'The fair value, as of the date of change of investment entity status, of subsidiaries that cease to be consolidated. [Refer: At fair value [member]; Disclosure of investment entities [text block]; Subsidiaries [member]]');
