	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RevenueFromContractsWithCustomers' ,N'Revenue from contracts with customers', N'The amount of revenue from contracts with customers. A customer is a party that has contracted with an entity to obtain goods or services that are an output of the entity’s ordinary activities in exchange for consideration.');
