	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContractAssets' ,N'Contract assets', N'The amount of an entity’s right to consideration in exchange for goods or services that the entity has transferred to a customer, when that right is conditioned on something other than the passage of time (for example, the entity’s future performance).');
