	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ImpairmentLoss' ,N'Impairment loss', N'The amount recognised as a reduction of the carrying amount of an asset or cash-generating unit to its recoverable amount. [Refer: Carrying amount [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReversalOfImpairmentLoss' ,N'Reversal of impairment loss', N'The amount recognised as an increase of the carrying amount of an asset or cash-generating unit to its recoverable amount when an impairment loss had been previously recognised. [Refer: Impairment loss]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfMainEventsAndCircumstancesThatLedToRecognitionOfImpairmentLossesAndReversalsOfImpairmentLosses' ,N'Explanation of main events and circumstances that led to recognition of impairment losses and reversals of impairment losses', N'The explanation of the main events and circumstances that led to the recognition of impairment losses and reversals of impairment losses. [Refer: Impairment loss]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfMainClassesOfAssetsAffectedByImpairmentLossesOrReversalsOfImpairmentLosses' ,N'Explanation of main classes of assets affected by impairment losses or reversals of impairment losses', N'The explanation of the main classes of assets affected by impairment losses or reversals of impairment losses. [Refer: Impairment loss]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnallocatedGoodwill' ,N'Unallocated goodwill', N'The amount of goodwill acquired in a business combination that has not been allocated to a cash-generating unit (group of units). [Refer: Goodwill; Cash-generating units [member]; Business combinations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfGoodwillNotAllocatedToCashgeneratingUnitExplanatory' ,N'Explanation of goodwill not allocated to cash-generating unit', N'The explanation of the reasons why a portion of the goodwill acquired in a business combination has not been allocated to a cash-generating unit (group of units). [Refer: Goodwill; Cash-generating units [member]; Business combinations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfFactThatCarryingAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesIsNotSignificant' ,N'Explanation of fact that carrying amount of goodwill or intangible assets with indefinite useful lives is not significant', N'The explanation of the fact that the carrying amount of goodwill or intangible assets with indefinite useful lives allocated to a unit (group of units) across multiple cash-generating units (groups of units) is not significant in comparison with the entity''s total carrying amount of goodwill or intangible assets with indefinite useful lives. [Refer: Carrying amount [member]; Cash-generating units [member]; Intangible assets other than goodwill]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfFactThatAggregateCarryingAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesAllocatedToRecoverableAmountsIsSignificant' ,N'Explanation of fact that aggregate carrying amount of goodwill or intangible assets with indefinite useful lives allocated to cash-generating units is significant', N'The explanation of the fact that the aggregate carrying amount of goodwill or intangible assets with indefinite useful lives allocated to the cash-generating unit (group of units) is significant in comparison with the entity''s total carrying amount of goodwill or intangible assets with indefinite useful lives. [Refer: Carrying amount [member]; Cash-generating units [member]; Intangible assets other than goodwill]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationForIndividualAssetOrCashgeneratingUnitWithSignificantAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesExplanatory' ,N'Disclosure of information for cash-generating units [text block]', N'The disclosure of information for cash-generating units. [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationForIndividualAssetOrCashgeneratingUnitWithSignificantAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesTable' ,N'Disclosure of information for cash-generating units [table]', N'Schedule disclosing information related to cash-generating units.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IndividualAssetsOrCashgeneratingUnitsWithSignificantAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesAxis' ,N'Cash-generating units [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForCashgeneratingUnitsMember' ,N'Entity''s total for cash-generating units [member]', N'This member stands for the standard value for the ''Cash-generating units'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IndividualAssetsOrCashgeneratingUnitsWithSignificantAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesMember' ,N'Cash-generating units [member]', N'This member stands for the smallest identifiable group of assets that generates cash inflows that are largely independent of the cash inflows from other assets or groups of assets.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AggregateNotSignificantIndividualAssetsOrCashgeneratingUnitsMember' ,N'Aggregate cash-generating units for which amount of goodwill or intangible assets with indefinite useful lives is not significant [member]', N'This member stands for the aggregate of cash-generating units for which the amount of goodwill or intangible assets with indefinite useful lives is not significant. [Refer: Cash-generating units [member]; Goodwill; Intangible assets other than goodwill]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationForIndividualAssetOrCashgeneratingUnitWithSignificantAmountOfGoodwillOrIntangibleAssetsWithIndefiniteUsefulLivesLineItems' ,N'Disclosure of information for cash-generating units [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Goodwill' ,N'Goodwill', N'The amount of assets representing the future economic benefits arising from other assets acquired in a business combination that are not individually identified and separately recognised. [Refer: Business combinations [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IntangibleAssetsWithIndefiniteUsefulLife' ,N'Intangible assets with indefinite useful life', N'The amount of intangible assets assessed as having an indefinite useful life. [Refer: Intangible assets other than goodwill]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOnWhichUnitsRecoverableAmountHasBeenDetermined' ,N'Description of basis on which unit''s recoverable amount has been determined', N'The description of the basis on which the cash-generating unit''s (group of units'') recoverable amount has been determined (ie value in use or fair value less costs of disposal). [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfKeyAssumptionsOnWhichManagementHasBasedCashFlowProjections' ,N'Description of key assumptions on which management has based cash flow projections', N'The description of key assumptions on which management has based its cash flow projections for the period covered by the most recent budgets/forecasts for a cash-generating unit (group of units). Key assumptions are those to which the unit''s (group of units'') recoverable amount is most sensitive. [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfValuationTechniquesUsedToMeasureFairValueLessCostsOfDisposal' ,N'Description of valuation techniques used to measure fair value less costs of disposal', N'The description of the valuation techniques used to measure fair value less costs of disposal for a cash generating unit (group of units). [Refer: Valuation techniques [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfKeyAssumptionsOnWhichManagementHasBasedDeterminationOfFairValueLessCostsOfDisposal' ,N'Description of key assumptions on which management has based determination of fair value less costs of disposal', N'The description of key assumptions on which management has based its determination of fair value less costs of disposal for a cash-generating unit (group of units). Key assumptions are those to which the unit''s (group of units'') recoverable amount is most sensitive. [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfManagementsApproachToDeterminingValuesAssignedToKeyAssumptions' ,N'Description of management''s approach to determining values assigned to key assumptions', N'The description of management''s approach to determining the value (or values) assigned to key assumptions, whether those value(s) reflect past experience or, if appropriate, are consistent with external sources of information and, if not, how and why they differ from past experience or external sources of information. Key assumptions are those to which the unit’s (group of units’) recoverable amount is most sensitive.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfLevelOfFairValueHierarchyWithinWhichFairValueMeasurementIsCategorised' ,N'Description of level of fair value hierarchy within which fair value measurement is categorised', N'The description of the level of the fair value hierarchy within which the fair value measurement is categorised in its entirety (without giving regard to the observability of ''costs of disposal'') for a cash-generating unit (group of units''). [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfChangeInValuationTechniqueUsedToMeasureFairValueLessCostsOfDisposal' ,N'Description of change in valuation technique used to measure fair value less costs of disposal', N'The description of a change in valuation technique used to measure fair value less costs of disposal. [Refer: Valuation techniques [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonsForChangeInValuationTechniqueUsedToMeasureFairValueLessCostsOfDisposal' ,N'Description of reasons for change in valuation technique used to measure fair value less costs of disposal', N'The description of the reasons for a change in valuation technique used to measure fair value less costs of disposal. [Refer: Valuation techniques [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfPeriodOverWhichManagementHasProjectedCashFlows' ,N'Explanation of period over which management has projected cash flows', N'The explanation of the period over which management has projected cash flows based on financial budgets/forecasts approved by management including, when a period greater than five years is used for a cash-generating unit (group of units), an explanation of why that longer period is justified. [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfGrowthRateUsedToExtrapolateCashFlowProjections' ,N'Growth rate used to extrapolate cash flow projections', N'The growth rate used to extrapolate cash flow projections beyond the period covered by the most recent budgets/forecasts for a cash-generating unit (group of units). [Refer: Cash-generating units [member]]');
