	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfDiscountRatesAppliedToCashFlowProjections' ,N'Discount rate applied to cash flow projections', N'The discount rate applied to cash flow projections for a cash-generating unit (group of units). [Refer: Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AmountByWhichUnitsRecoverableAmountExceedsItsCarryingAmount' ,N'Amount by which unit''s recoverable amount exceeds its carrying amount', N'The amount by which a cash-generating unit''s (group of units'') recoverable amount exceeds its carrying amount. [Refer: Carrying amount [member]; Cash-generating units [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfValueAssignedToKeyAssumption' ,N'Explanation of value assigned to key assumption', N'The explanation of the value(s) assigned to key assumption(s) on which management has based its determination of recoverable amount for a cash-generating unit (group of units). [Refer: Cash-generating units [member]]');
