	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfLeasesExplanatory' ,N'Disclosure of leases [text block]', N'The entire disclosure for leases.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RightofuseAssetsThatDoNotMeetDefinitionOfInvestmentProperty' ,N'Right-of-use assets that do not meet definition of investment property', N'The amount of right-of-use assets that do not meet the definition of investment property. [Refer: Right-of-use assets; Investment property]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfLineItemsInStatementOfFinancialPositionWhichIncludeRightofuseAssets' ,N'Description of line items in statement of financial position which include right-of-use assets', N'The description of the line items in the statement of financial position that include the right-of-use assets. [Refer: Right-of-use assets]');
