	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PropertyPlantAndEquipmentMember' ,N'Property, plant and equipment [member]', N'This member stands for property, plant and equipment. It also represents the standard value for the ''Classes of property, plant and equipment'' axis if no other member is used. [Refer: Property, plant and equipment]');
