	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ComputerEquipmentMember' ,N'Computer equipment [member]', N'This member stands for a class of property, plant and equipment representing computer equipment. [Refer: Property, plant and equipment]');
