	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'BearerPlantsMember' ,N'Bearer plants [member]', N'This member stands for a class of property, plant and equipment representing bearer plants. Bearer plant is a living plant that (a) is used in the production or supply of agricultural produce; (b) is expected to bear produce for more than one period; and (c) has a remote likelihood of being sold as agricultural produce, except for incidental scrap sales. [Refer: Property, plant and equipment]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MiningAssetsMember' ,N'Mining assets [member]', N'This member stands for mining assets. [Refer: Mining assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MiningPropertyMember' ,N'Mining property [member]', N'This member stands for property related to mining activities.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OilAndGasAssetsMember' ,N'Oil and gas assets [member]', N'This member stands for oil and gas assets. [Refer: Oil and gas assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PowerGeneratingAssetsMember' ,N'Power generating assets [member]', N'This member stands for a class of property, plant and equipment representing assets that generate power. [Refer: Property, plant and equipment]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ConstructionInProgressMember' ,N'Construction in progress [member]', N'This member stands for expenditure capitalised during the construction of items of property, plant and equipment that are not yet available for use (ie not yet in the location and condition necessary for it to be capable of operating in the manner intended by the management). [Refer: Property, plant and equipment]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OwneroccupiedPropertyMeasuredUsingInvestmentPropertyFairValueModelMember' ,N'Owner-occupied property measured using investment property fair value model [member]', N'This member stands for a class of property, plant and equipment representing owner-occupied property measured using the investment property fair value model applying paragraph 29A of IAS 16. [Refer: Property, plant and equipment]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherPropertyPlantAndEquipmentMember' ,N'Other property, plant and equipment [member]', N'This member stands for a class of property, plant and equipment representing property, plant and equipment that the entity does not separately disclose in the same statement or note. [Refer: Property, plant and equipment]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IntangibleAssetsOtherThanGoodwillMember' ,N'Intangible assets other than goodwill [member]', N'This member stands for intangible assets other than goodwill. It also represents the standard value for the ''Classes of intangible assets other than goodwill'' axis if no other member is used. [Refer: Intangible assets other than goodwill]');
