	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EffectiveDateOfRevaluationRightofuseAssets' ,N'Effective date of revaluation, right-of-use assets', N'The effective date of the revaluation for right-of-use assets stated at revalued amounts. [Refer: Right-of-use assets]');
