	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseRelatingToShorttermLeasesForWhichRecognitionExemptionHasBeenUsed' ,N'Expense relating to short-term leases for which recognition exemption has been used', N'The amount of the expense relating to short-term leases accounted for applying paragraph 6 of Ifrs 16. This expense need not include the expense relating to leases with a lease term of one month or less. Short-term lease is a lease that, at the commencement date, has a lease term of 12 months or less. A lease that contains a purchase option is not a short-term lease.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseRelatingToLeasesOfLowvalueAssetsForWhichRecognitionExemptionHasBeenUsed' ,N'Expense relating to leases of low-value assets for which recognition exemption has been used', N'The amount of the expense relating to leases of low-value assets accounted for applying paragraph 6 of Ifrs 16. This expense shall not include the expense relating to short-term leases of low-value assets.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseRelatingToVariableLeasePaymentsNotIncludedInMeasurementOfLeaseLiabilities' ,N'Expense relating to variable lease payments not included in measurement of lease liabilities', N'The amount of the expense relating to variable lease payments not included in the measurement of lease liabilities. Variable lease payments are the portion of payments made by a lessee to a lessor for the right to use an underlying asset during the lease term that varies because of changes in facts or circumstances occurring after the commencement date, other than the passage of time. [Refer: Lease liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeFromSubleasingRightofuseAssets' ,N'Income from subleasing right-of-use assets', N'The amount of income from subleasing right-of-use assets. Sublease is a transaction for which an underlying asset is re-leased by a lessee (‘intermediate lessor’) to a third party, and the lease (‘head lease’) between the head lessor and lessee remains in effect. [Refer: Right-of-use assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CashOutflowForLeases' ,N'Cash outflow for leases', N'The cash outflow for leases.');
