	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsLossesArisingFromSaleAndLeasebackTransactions' ,N'Gains (losses) arising from sale and leaseback transactions', N'The gains (losses) arising from sale and leaseback transactions.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LeaseCommitmentsForShorttermLeasesForWhichRecognitionExemptionHasBeenUsed' ,N'Lease commitments for short-term leases for which recognition exemption has been used', N'The amount of the lease commitments for short-term leases accounted for applying paragraph 6 of Ifrs 16. Short-term lease is a lease that, at the commencement date, has a lease term of 12 months or less. A lease that contains a purchase option is not a short-term lease.');
