	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutLesseesExposureArisingFromVariableLeasePayments' ,N'Information about lessee''s exposure arising from variable lease payments', N'Information about the lessee''s exposure arising from variable lease payments. Variable lease payments are the portion of payments made by a lessee to a lessor for the right to use an underlying asset during the lease term that varies because of changes in facts or circumstances occurring after the commencement date, other than the passage of time.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutLesseesExposureArisingFromExtensionOptionsAndTerminationOptions' ,N'Information about lessee''s exposure arising from extension options and termination options', N'Information about the lessee''s exposure arising from extension options and termination options.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutLesseesExposureArisingFromResidualValueGuarantees' ,N'Information about lessee''s exposure arising from residual value guarantees', N'Information about the lessee''s exposure arising from residual value guarantees. Residual value guarantee is a guarantee made to a lessor by a party unrelated to the lessor that the value (or part of the value) of an underlying asset at the end of a lease will be at least a specified amount.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutExposureArisingFromLeasesNotYetCommencedToWhichLesseeIsCommitted' ,N'Information about exposure arising from leases not yet commenced to which lessee is committed', N'Information about the lessee''s exposure arising from leases not yet commenced to which the lessee is committed.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutRestrictionsOrCovenantsImposedByLeasesOnLessee' ,N'Information about restrictions or covenants imposed by leases on lessee', N'Information about the restrictions or covenants imposed by leases on the lessee.');
