	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SellingProfitLossOnFinanceLeases' ,N'Selling profit (loss) on finance leases', N'The selling profit (loss) on finance leases. Finance lease is a lease that transfers substantially all the risks and rewards incidental to ownership of an underlying asset.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinanceIncomeOnNetInvestmentInFinanceLease' ,N'Finance income on net investment in finance lease', N'The amount of finance income on the net investment in the finance lease. [Refer: Finance income; Net investment in finance lease]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeRelatingToVariableLeasePaymentsNotIncludedInMeasurementOfNetInvestmentInFinanceLease' ,N'Income relating to variable lease payments not included in measurement of net investment in finance lease', N'The amount of income relating to variable lease payments not included in the measurement of the net investment in the finance lease. Variable lease payments are the portion of payments made by a lessee to a lessor for the right to use an underlying asset during the lease term that varies because of changes in facts or circumstances occurring after the commencement date, other than the passage of time. [Refer: Net investment in finance lease]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OperatingLeaseIncome' ,N'Operating lease income', N'The amount of operating lease income. Operating lease is a lease that does not transfer substantially all the risks and rewards incidental to ownership of an underlying asset.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeRelatingToVariableLeasePaymentsForOperatingLeasesThatDoNotDependOnIndexOrRate' ,N'Income relating to variable lease payments for operating leases that do not depend on index or rate', N'The amount of income relating to variable lease payments for operating leases that do not depend on an index or a rate. Variable lease payments are the portion of payments made by a lessee to a lessor for the right to use an underlying asset during the lease term that varies because of changes in facts or circumstances occurring after the commencement date, other than the passage of time.');
