	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutRiskManagementStrategyForRightsThatLessorRetainsInUnderlyingAssets' ,N'Information about risk management strategy for rights that lessor retains in underlying assets', N'Information about the risk management strategy for the rights that the lessor retains in underlying assets, including any means by which the lessor reduces that risk.');
