	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UndiscountedFinanceLeasePaymentsToBeReceived' ,N'Undiscounted finance lease payments to be received', N'The amount of undiscounted finance lease payments to be received. Finance lease is a lease that transfers substantially all the risks and rewards incidental to ownership of an underlying asset.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnearnedFinanceIncomeRelatingToFinanceLeasePaymentsReceivable' ,N'Unearned finance income relating to finance lease payments receivable', N'The amount of unearned finance income relating to the finance lease payments receivable. Finance lease is a lease that transfers substantially all the risks and rewards incidental to ownership of an underlying asset. [Refer: Finance income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DiscountedUnguaranteedResidualValueOfAssetsSubjectToFinanceLease' ,N'Discounted unguaranteed residual value of assets subject to finance lease', N'The amount of the discounted unguaranteed residual value of assets subject to finance leases. Unguaranteed residual value is the portion of the residual value of the underlying asset, the realisation of which by a lessor is not assured or is guaranteed solely by a party related to the lessor. Finance lease is a lease that transfers substantially all the risks and rewards incidental to ownership of an underlying asset.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NetInvestmentInFinanceLease' ,N'Net investment in finance lease', N'The gross investment in the finance lease discounted at the interest rate implicit in the lease. Finance lease is a lease that transfers substantially all the risks and rewards incidental to ownership of an underlying asset. Gross investment in the finance lease is the sum of: (a) the lease payments receivable by a lessor under a finance lease; and (b) any unguaranteed residual value accruing to the lessor. Interest rate implicit in the lease is the rate of interest that causes the present value of (a) the lease payments and (b) the unguaranteed residual value to equal to the sum of (i) the fair value of the underlying asset and (ii) the initial direct costs of the lessor.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfMaturityAnalysisOfOperatingLeasePaymentsExplanatory' ,N'Disclosure of maturity analysis of operating lease payments [text block]', N'The disclosure of a maturity analysis of operating lease payments. Operating lease is a lease that does not transfer substantially all the risks and rewards incidental to ownership of an underlying asset.');
