	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WeightedAverageLesseesIncrementalBorrowingRateAppliedToLeaseLiabilitiesRecognisedAtDateOfInitialApplicationOfIfrs16' ,N'Weighted average lessee''s incremental borrowing rate applied to lease liabilities recognised at date of initial application of Ifrs 16', N'The weighted average lessee''s incremental borrowing rate applied to lease liabilities recognised in the statement of financial position at the date of initial application of Ifrs 16. The incremental borrowing rate is the rate of interest that a lessee would have to pay to borrow over a similar term, and with a similar security, the funds necessary to obtain an asset of a similar value to the right-of-use asset in a similar economic environment.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfDifferenceBetweenOperatingLeaseCommitmentsDisclosedApplyingIAS17AndLeaseLiabilitiesRecognisedAtDateOfInitialApplicationOfIfrs16Explanatory' ,N'Explanation of difference between operating lease commitments disclosed applying IAS 17 and lease liabilities recognised at date of initial application of Ifrs 16 [text block]', N'The explanation of the difference between: (a) operating lease commitments disclosed applying IAS 17 at the end of the annual reporting period immediately preceding the date of initial application of Ifrs 16, discounted using the incremental borrowing rate at the date of initial application; and (b) lease liabilities recognised in the statement of financial position at the date of initial application of Ifrs 16. The incremental borrowing rate is the rate of interest that a lessee would have to pay to borrow over a similar term, and with a similar security, the funds necessary to obtain an asset of a similar value to the right-of-use asset in a similar economic environment.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'StatementThatLesseeUsesPracticalExpedientsWhenApplyingIfrs16RetrospectivelyToLeasesClassifiedAsOperatingLeasesApplyingIAS17' ,N'Statement that lessee uses practical expedients when applying Ifrs 16 retrospectively to leases classified as operating leases applying IAS 17', N'The statement that the lessee uses one or more of the specified practical expedients in paragraph C10 of Ifrs 16 when applying Ifrs 16 retrospectively in accordance with paragraph C5(b) to leases classified as operating leases applying IAS 17.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfServiceConcessionArrangementsExplanatory' ,N'Disclosure of service concession arrangements [text block]', N'The entire disclosure for service concession arrangements.');
