	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfNatureAndExtentOfObligationsToAcquireOrBuildItemsOfPropertyPlantAndEquipment' ,N'Explanation of nature and extent of obligations to acquire or build items of property, plant and equipment', N'The explanation of the nature and extent (for example, quantity, time period or amount as appropriate) of obligations to acquire or build items of property, plant and equipment in service concession arrangements. [Refer: Service concession arrangements [member]; Property, plant and equipment]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfNatureAndExtentOfObligationsToDeliverOrRightsToReceiveSpecifiedAssetsAtEndOfConcessionPeriod' ,N'Explanation of nature and extent of obligations to deliver or rights to receive specified assets at end of concession period', N'The explanation of the nature and extent (for example, quantity, time period or amount as appropriate) of obligations to deliver or rights to receive specified assets at the end of the concession period in service concession arrangements. [Refer: Service concession arrangements [member]]');
