	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationOnHowIncrementalFairValueGrantedWasMeasuredModifiedSharebasedPaymentArrangements' ,N'Information on how incremental fair value granted was measured, modified share-based payment arrangements', N'Information about how the incremental fair value granted was measured for modified share-based payment arrangements. [Refer: Incremental fair value granted, modified share-based payment arrangements; Share-based payment arrangements [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfDirectMeasurementOfFairValueOfGoodsOrServicesReceived' ,N'Explanation of direct measurement of fair value of goods or services received', N'The explanation of how the fair value of goods or services received was determined if it was measured directly (for example, whether fair value was measured at a market price for those goods or services).');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonWhyFairValueOfGoodsOrServicesReceivedCannotEstimateReliable' ,N'Description of reason why fair value of goods or services received cannot be reliably estimated', N'The description of the reason why the presumption that the fair value of goods or services received in equity-settled share-based payment transactions with parties other than employees can be estimated reliably was rebutted.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfEffectOfSharebasedPaymentsOnProfitOrLoss' ,N'Explanation of effect of share-based payments on entity''s profit or loss [text block]', N'The explanation that enables users of financial statements to understand the effect of share-based payment transactions on the entity''s profit (loss).');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseFromEquitysettledSharebasedPaymentTransactionsInWhichGoodsOrServicesReceivedDidNotQualifyForRecognitionAsAssets' ,N'Expense from equity-settled share-based payment transactions in which goods or services received did not qualify for recognition as assets', N'The amount of expense arising from equity-settled share-based payment transactions in which the goods or services received did not qualify for recognition as assets. [Refer: Expense from share-based payment transactions in which goods or services received did not qualify for recognition as assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseFromCashsettledSharebasedPaymentTransactionsInWhichGoodsOrServicesReceivedDidNotQualifyForRecognitionAsAssets' ,N'Expense from cash-settled share-based payment transactions in which goods or services received did not qualify for recognition as assets', N'The amount of expense arising from cash-settled share-based payment transactions in which the goods or services received did not qualify for recognition as assets. [Refer: Expense from share-based payment transactions in which goods or services received did not qualify for recognition as assets]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseFromSharebasedPaymentTransactionsInWhichGoodsOrServicesReceivedDidNotQualifyForRecognitionAsAssets' ,N'Expense from share-based payment transactions in which goods or services received did not qualify for recognition as assets', N'The amount of expense arising from share-based payment transactions in which the goods or services received did not qualify for recognition as assets.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpenseFromSharebasedPaymentTransactionsWithEmployees' ,N'Expense from share-based payment transactions with employees', N'The amount of expense from share-based payment transactions with employees.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExplanationOfEffectOfSharebasedPaymentsOnFinancialPositions' ,N'Explanation of effect of share-based payments on entity''s financial position [text block]', N'The explanation that enables users of financial statements to understand the effect of share-based payment transactions on the entity''s financial position.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiabilitiesFromSharebasedPaymentTransactions2011' ,N'Liabilities from share-based payment transactions', N'The amount of liabilities arising from share-based payment transactions. Share-based payment transactions are transactions in which the entity: (a) receives goods or services from the supplier of those goods or services (including an employee) in a share-based payment arrangement; or (b) incurs an obligation to settle the transaction with the supplier in a share-based payment arrangement when another group entity receives those goods or services. [Refer: Share-based payment arrangements [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IntrinsicValueOfLiabilitiesFromSharebasedPaymentTransactionsForWhichCounterpartysRightToCashOrOtherAssetsVested2011' ,N'Intrinsic value of liabilities from share-based payment transactions for which counterparty''s right to cash or other assets vested', N'The intrinsic value of liabilities arising from share-based transactions for which the counterparty''s right to cash or other assets had vested by the end of the period (for example, vested share appreciation rights). The intrinsic value is the difference between the fair value of the shares to which the counterparty has the (conditional or unconditional) right to subscribe, or which it has the right to receive, and the price (if any) that the counterparty is (or will be) required to pay for those shares. Share-based payment transactions are transactions in which the entity: (a) receives goods or services from the supplier of those goods or services (including an employee) in a share-based payment arrangement; or (b) incurs an obligation to settle the transaction with the supplier in a share-based payment arrangement when another group entity receives those goods or services, for which the counterparty''s right to cash or other assets had vested by the end of the period (for example, vested share appreciation rights).');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AdditionalInformationAboutSharebasedPaymentArrangements' ,N'Additional information about share-based payment arrangements [text block]', N'Additional information about share-based payment arrangements necessary to satisfy the disclosure requirements of Ifrs 2. [Refer: Share-based payment arrangements [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfEmployeeBenefitsExplanatory' ,N'Disclosure of employee benefits [text block]', N'The entire disclosure for employee benefits.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDefinedBenefitPlansExplanatory' ,N'Disclosure of defined benefit plans [text block]', N'The disclosure of defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDefinedBenefitPlansTable' ,N'Disclosure of defined benefit plans [table]', N'Schedule disclosing information related to defined benefit plans.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansAxis' ,N'Defined benefit plans [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansMember' ,N'Defined benefit plans [member]', N'This member stands for post-employment benefit plans other than defined contribution plans. Defined contribution plans are post-employment benefit plans under which an entity pays fixed contributions into a separate entity (a fund) and will have no legal or constructive obligation to pay further contributions if the fund does not hold sufficient assets to pay all employee benefits relating to employee service in the current and prior periods. It also represents the standard value for the ''Defined benefit plans'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansOtherThanMultiemployerPlansStatePlansAndPlansThatShareRisksBetweenEntitiesUnderCommonControlMember' ,N'Defined benefit plans other than multi-employer plans, state plans and plans that share risks between entities under common control [member]', N'This member stands for defined benefit plans other than multi-employer plans, state plans and plans that share risks between entities under common control. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MultiemployerDefinedBenefitPlansMember' ,N'Multi-employer defined benefit plans [member]', N'This member stands for defined benefit plans (other than state plans) that: (a) pool the assets contributed by various entities that are not under common control; and (b) use those assets to provide benefits to employees of more than one entity, on the basis that contribution and benefit levels are determined without regard to the identity of the entity that employs the employees. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'StateDefinedBenefitPlansMember' ,N'State defined benefit plans [member]', N'This member stands for defined benefit plans that are established by legislation to cover all entities (or all entities in a particular category) and are operated by national or local government or by another body that is not subject to control or influence by the reporting entity. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansThatShareRisksBetweenEntitiesUnderCommonControlMember' ,N'Defined benefit plans that share risks between entities under common control [member]', N'This member stands for defined benefit plans that share risks between entities under common control such as a parent and its subsidiaries. [Refer: Defined benefit plans [member]; Subsidiaries [member]; Parent [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GeographicalAreasAxis' ,N'Geographical areas [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GeographicalAreasMember' ,N'Geographical areas [member]', N'This member stands for aggregated geographical areas. It also represents the standard value for the ''Geographical areas'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CharacteristicsOfDefinedBenefitPlansAxis' ,N'Characteristics of defined benefit plans [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CharacteristicsOfDefinedBenefitPlansMember' ,N'Characteristics of defined benefit plans [member]', N'This member stands for all defined benefit plans when disaggregated by characteristics of defined benefits plans. It also represents the standard value for the ''Characteristics of defined benefits plans'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PensionDefinedBenefitPlansMember' ,N'Pension defined benefit plans [member]', N'This member stands for pension defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FlatSalaryPensionDefinedBenefitPlansMember' ,N'Flat salary pension defined benefit plans [member]', N'This member stands for flat salary pension defined benefit plans. [Refer: Pension defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinalSalaryPensionDefinedBenefitPlansMember' ,N'Final salary pension defined benefit plans [member]', N'This member stands for final salary pension defined benefit plans. [Refer: Pension defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PostemploymentMedicalDefinedBenefitPlansMember' ,N'Post-employment medical defined benefit plans [member]', N'This member stands for post-employment medical defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RegulatoryEnvironmentsAxis' ,N'Regulatory environments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RegulatoryEnvironmentsMember' ,N'Regulatory environments [member]', N'This member stands for all regulatory environments. It also represents the standard value for the ''Regulatory environments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentsAxis' ,N'Segments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentsMember' ,N'Segments [member]', N'This member stands for all segments of an entity. It also represents the standard value for the ''Segments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReportableSegmentsMember' ,N'Reportable segments [member]', N'This member stands for operating segments for which Ifrs 8 requires information to be disclosed. The entity shall report separately information about an operating segment that meets any of the following quantitative thresholds: (a) reported revenue, including both sales to external customers and intersegment sales or transfers, is 10 per cent or more of the combined revenue, internal and external, of all operating segments; (b) the absolute amount of its reported profit or loss is 10 per cent or more of the greater, in absolute amount, of (i) the combined reported profit of all operating segments that did not report a loss and (ii) the combined reported loss of all operating segments that reported a loss; or (c) assets are 10 per cent or more of the combined assets of all operating segments. Additionally operating segments that do not meet any of the quantitative thresholds may be considered reportable, and separately disclosed, if management believes that information about the segment would be useful to users of the financial statements. [Refer: Operating segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FundingArrangementsOfDefinedBenefitPlansAxis' ,N'Funding arrangements of defined benefit plans [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FundingArrangementsOfDefinedBenefitPlansMember' ,N'Funding arrangements of defined benefit plans [member]', N'This member stands for all defined benefit plans when disaggregated by funding arrangements of defined benefits plans. It also represents the standard value for the ''Funding arrangements of defined benefits plans'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WhollyUnfundedDefinedBenefitPlansMember' ,N'Wholly unfunded defined benefit plans [member]', N'This member stands for wholly unfunded defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WhollyOrPartlyFundedDefinedBenefitPlansMember' ,N'Wholly or partly funded defined benefit plans [member]', N'This member stands for wholly funded or partly funded defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDefinedBenefitPlansLineItems' ,N'Disclosure of defined benefit plans [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTypeOfPlan' ,N'Description of type of plan', N'The general description of the type of a defined benefit plan. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfBenefitsProvidedByPlan' ,N'Description of nature of benefits provided by plan', N'The description of the nature of the benefits provided by a defined benefit plan (for example, final salary defined benefit plan or contribution-based plan with guarantee). [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfRegulatoryFrameworkInWhichPlanOperates' ,N'Description of regulatory framework in which plan operates', N'The description of the regulatory framework in which a defined benefit plan operates, for example, the level of any minimum funding requirements. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfEffectOfRegulatoryFrameworkOnPlan' ,N'Description of effect of regulatory framework on plan', N'The description of the effect of the regulatory framework on a defined benefit plan, such as the asset ceiling. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAnyOtherEntitysResponsibilitiesForGovernanceOfPlan' ,N'Description of any other entity''s responsibilities for governance of plan', N'The description of the entity''s responsibilities for the governance of a defined benefit plan that the entity does not separately describe, for example, responsibilities of trustees or of board members of the plan. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfRisksToWhichPlanExposesEntity' ,N'Description of risks to which plan exposes entity', N'The description of the risks to which the defined benefit plan exposes the entity, focused on any unusual, entity-specific or plan-specific risks. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfSignificantConcentrationsOfRiskRelatedToPlan' ,N'Description of significant concentrations of risk related to plan', N'The description of the significant concentrations of risk in defined benefit plans. For example, if plan assets are invested primarily in one class of investments, such as property, the plan may expose the entity to a concentration of property market risk. [Refer: Defined benefit plans [member]; Market risk [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfPlanAmendmentsCurtailmentsAndSettlements' ,N'Description of plan amendments, curtailments and settlements', N'The description of defined benefit plan amendments, curtailments and settlements. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitObligationAtPresentValue' ,N'Defined benefit obligation, at present value', N'The present value, without deducting any plan assets, of expected future payments required to settle the obligation resulting from employee service in the current and prior periods. [Refer: Plan assets, at fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PlanAssetsAtFairValue' ,N'Plan assets, at fair value', N'The fair value of defined benefit plan assets. Plan assets comprise assets held by a long-term employee benefit fund and qualifying insurance policies. [Refer: At fair value [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SurplusDeficitInPlan' ,N'Surplus (deficit) in plan', N'The fair value of any plan assets, less the present value of the defined benefit obligation. [Refer: Plan assets [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfLinkBetweenReimbursementRightAndRelatedObligation' ,N'Description of link between reimbursement right and related obligation', N'The description of the link between a reimbursement right and the related obligation. [Refer: Reimbursement rights, at fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfHowEntityDeterminedMaximumEconomicBenefitAvailable' ,N'Description of how entity determined maximum economic benefit available', N'The description of how the entity determined the maximum economic benefit available in relation to a defined benefit plan, ie whether those benefits would be in the form of refunds, reductions in future contributions or a combination of both. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAmountsOfEntitysOwnFinancialInstrumentsIncludedInFairValueOfPlanAssets' ,N'Entity''s own financial instruments included in fair value of plan assets', N'The fair value of the entity''s own transferable financial instruments that are included in the fair value of defined benefit plan assets. [Refer: Plan assets, at fair value; Financial instruments, class [member]; Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAmountsOfPropertyOccupiedByEntityIncludedInFairValueOfPlanAssets' ,N'Property occupied by entity included in fair value of plan assets', N'The fair value of property occupied by the entity that are included in the fair value of defined benefit plan assets. [Refer: Plan assets, at fair value; Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAmountsOfOtherAssetsUsedByEntityIncludedInFairValueOfPlanAssets' ,N'Other assets used by entity included in fair value of plan assets', N'The fair value of other assets used by the entity that are included in the fair value of defined benefit plan assets. [Refer: Plan assets, at fair value; Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfDiscountRates' ,N'Actuarial assumption of discount rates', N'The discount rate used as the significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfExpectedRatesOfSalaryIncreases' ,N'Actuarial assumption of expected rates of salary increases', N'The expected rate of salary increases used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit plans [member]; Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfMedicalCostTrendRates' ,N'Actuarial assumption of medical cost trend rates', N'Medical cost trend rate used as significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfExpectedRatesOfPensionIncreases' ,N'Actuarial assumption of expected rates of pension increases', N'The expected rate of increases in pension that is used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit plans [member]; Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfExpectedRatesOfInflation' ,N'Actuarial assumption of expected rates of inflation', N'The expected rate of inflation that is used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit plans [member]; Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfMortalityRates' ,N'Actuarial assumption of mortality rates', N'The mortality rate used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfRetirementAge2019' ,N'Actuarial assumption of retirement age', N'The retirement age used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialAssumptionOfLifeExpectancyAfterRetirement2019' ,N'Actuarial assumption of life expectancy after retirement', N'The life expectancy after retirement used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Defined benefit obligation, at present value; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherMaterialActuarialAssumptions' ,N'Other material actuarial assumptions', N'Any other material assumption used as a significant actuarial assumption to determine the present value of a defined benefit obligation. [Refer: Actuarial assumptions [member]; Defined benefit obligation, at present value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAssetliabilityMatchingStrategiesUsedByPlanOrEntityToManageRisk' ,N'Description of asset-liability matching strategies used by plan or entity to manage risk', N'The description of asset-liability matching strategies used by the defined benefit plan or the entity, including the use of annuities and other techniques, such as longevity swaps, to manage risk. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfFundingArrangementsAndFundingPolicyThatAffectFutureContributions' ,N'Description of funding arrangements and funding policy that affect future contributions', N'The description of funding arrangements and the funding policy that affect future contributions to defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EstimateOfContributionsExpectedToBePaidToPlan' ,N'Estimate of contributions expected to be paid to plan for next annual reporting period', N'The estimate of the expected contributions to be made to a defined benefit plan for the next annual reporting period. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInformationAboutMaturityProfileOfDefinedBenefitObligationExplanatory' ,N'Disclosure of information about maturity profile of defined benefit obligation [text block]', N'The disclosure of information about the maturity profile of a defined benefit obligation. This will include the weighted average duration of the defined benefit obligation and may include other information about the distribution of the timing of benefit payments, such as a maturity analysis of the benefit payments. [Refer: Defined benefit obligation, at present value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WeightedAverageDurationOfDefinedBenefitObligation2019' ,N'Weighted average duration of defined benefit obligation', N'The weighted average duration of a defined benefit obligation. [Refer: Weighted average [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfExtentToWhichEntityCanBeLiableToMultiemployerOrStatePlanForOtherEntitiesObligations' ,N'Description of extent to which entity can be liable to multi-employer or state plan for other entities'' obligations', N'The description of the extent to which the entity can be liable for other entities'' obligations under the terms and conditions of a multi-employer or state defined benefit plan. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAgreedAllocationOfDeficitOrSurplusOfMultiemployerOrStatePlanOnWindupOfPlan' ,N'Description of agreed allocation of deficit or surplus of multi-employer or state plan on wind-up of plan', N'The description of any agreed allocation of a deficit or surplus on the wind-up of a multi-employer or state defined benefit plan. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfAgreedAllocationOfDeficitOrSurplusOfMultiemployerOrStatePlanOnEntitysWithdrawalFromPlan' ,N'Description of agreed allocation of deficit or surplus of multi-employer or state plan on entity''s withdrawal from plan', N'The description of any agreed allocation of a deficit or surplus on the entity''s withdrawal from a multi-employer or state defined benefit plan. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfFactThatMultiemployerPlanIsDefinedBenefitPlan' ,N'Description of fact that multi-employer or state plan is defined benefit plan', N'The description of the fact that a multi-employer or state plan is a defined benefit plan. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfReasonWhySufficientInformationIsNotAvailableToAccountForMultiemployerPlanAsDefinedBenefitPlan' ,N'Description of reason why sufficient information is not available to account for multi-employer or state plan as defined benefit plan', N'The description of the reason why sufficient information is not available to enable the entity to account for a multi-employer or state plan as a defined benefit plan. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfInformationAboutSurplusOrDeficitOfMultiemployerPlan' ,N'Description of information about surplus or deficit of multi-employer or state plan', N'The description of available information about a surplus or deficit in a multi-employer or state plan that may affect the amount of future contributions. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisUsedToDetermineSurplusOrDeficitOfMultiemployerPlan' ,N'Description of basis used to determine surplus or deficit of multi-employer or state plan', N'The description of the basis used to determine a surplus or deficit in a multi-employer or state plan that may affect the amount of future contributions. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ImplicationsOfSurplusOrDeficitOnMultiemployerPlanForEntity' ,N'Description of implications of surplus or deficit on multi-employer or state plan for entity', N'The description of the implications for the entity of a surplus or deficit in a multi-employer or state plan that may affect the amount of future contributions. [Refer: Multi-employer defined benefit plans [member]; State defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LevelOfParticipationOfEntityComparedWithOtherParticipatingEntities' ,N'Level of participation of entity compared with other participating entities', N'The description of an indication of the level of participation of the entity in a defined benefit plan compared with other participating entities. Examples of measures that might provide such an indication include the entity''s proportion of the total contributions to the plan or the entity''s proportion of the total number of active members, retired members, and former members entitled to benefits, if that information is available. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfContractualAgreementOrStatedPolicyForChargingNetDefinedBenefitCost' ,N'Description of contractual agreement or stated policy for charging net defined benefit cost', N'The description of the contractual agreement or stated policy for charging the net defined benefit cost, or the fact that there is no such policy, for an entity participating in defined benefit plans that share risks between various entities under common control. [Refer: Defined benefit plans that share risks between entities under common control [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfPolicyForDeterminingContributionOfDefinedBenefitPlansThatShareRisksBetweenVariousEntities' ,N'Description of policy for determining contribution of defined benefit plans that share risks between entities under common control [text block]', N'The description of the policy for determining the contribution to be paid by the entity for defined benefit plans that share risks between entities under common control. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfCrossreferenceToDisclosuresAboutPlansThatShareRisksBetweenEntitiesUnderCommonControlInAnotherGroupEntitysFinancialStatements' ,N'Description of cross-reference to disclosures about plans that share risks between entities under common control in another group entity''s financial statements', N'The description of cross-references to disclosures about defined benefit plans that share risks between entities under common control in another group entity''s financial statements. [Refer: Defined benefit plans that share risks between entities under common control [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfNetDefinedBenefitLiabilityAssetExplanatory' ,N'Disclosure of net defined benefit liability (asset) [text block]', N'The disclosure of a net defined benefit liability (asset). [Refer: Net defined benefit liability (asset)]');
