	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansAxis' ,N'Defined benefit plans [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansMember' ,N'Defined benefit plans [member]', N'This member stands for post-employment benefit plans other than defined contribution plans. Defined contribution plans are post-employment benefit plans under which an entity pays fixed contributions into a separate entity (a fund) and will have no legal or constructive obligation to pay further contributions if the fund does not hold sufficient assets to pay all employee benefits relating to employee service in the current and prior periods. It also represents the standard value for the ''Defined benefit plans'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansOtherThanMultiemployerPlansStatePlansAndPlansThatShareRisksBetweenEntitiesUnderCommonControlMember' ,N'Defined benefit plans other than multi-employer plans, state plans and plans that share risks between entities under common control [member]', N'This member stands for defined benefit plans other than multi-employer plans, state plans and plans that share risks between entities under common control. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MultiemployerDefinedBenefitPlansMember' ,N'Multi-employer defined benefit plans [member]', N'This member stands for defined benefit plans (other than state plans) that: (a) pool the assets contributed by various entities that are not under common control; and (b) use those assets to provide benefits to employees of more than one entity, on the basis that contribution and benefit levels are determined without regard to the identity of the entity that employs the employees. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'StateDefinedBenefitPlansMember' ,N'State defined benefit plans [member]', N'This member stands for defined benefit plans that are established by legislation to cover all entities (or all entities in a particular category) and are operated by national or local government or by another body that is not subject to control or influence by the reporting entity. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DefinedBenefitPlansThatShareRisksBetweenEntitiesUnderCommonControlMember' ,N'Defined benefit plans that share risks between entities under common control [member]', N'This member stands for defined benefit plans that share risks between entities under common control such as a parent and its subsidiaries. [Refer: Defined benefit plans [member]; Subsidiaries [member]; Parent [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GeographicalAreasAxis' ,N'Geographical areas [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
