	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CharacteristicsOfDefinedBenefitPlansAxis' ,N'Characteristics of defined benefit plans [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CharacteristicsOfDefinedBenefitPlansMember' ,N'Characteristics of defined benefit plans [member]', N'This member stands for all defined benefit plans when disaggregated by characteristics of defined benefits plans. It also represents the standard value for the ''Characteristics of defined benefits plans'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PensionDefinedBenefitPlansMember' ,N'Pension defined benefit plans [member]', N'This member stands for pension defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FlatSalaryPensionDefinedBenefitPlansMember' ,N'Flat salary pension defined benefit plans [member]', N'This member stands for flat salary pension defined benefit plans. [Refer: Pension defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinalSalaryPensionDefinedBenefitPlansMember' ,N'Final salary pension defined benefit plans [member]', N'This member stands for final salary pension defined benefit plans. [Refer: Pension defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PostemploymentMedicalDefinedBenefitPlansMember' ,N'Post-employment medical defined benefit plans [member]', N'This member stands for post-employment medical defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RegulatoryEnvironmentsAxis' ,N'Regulatory environments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RegulatoryEnvironmentsMember' ,N'Regulatory environments [member]', N'This member stands for all regulatory environments. It also represents the standard value for the ''Regulatory environments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentsAxis' ,N'Segments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentsMember' ,N'Segments [member]', N'This member stands for all segments of an entity. It also represents the standard value for the ''Segments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReportableSegmentsMember' ,N'Reportable segments [member]', N'This member stands for operating segments for which Ifrs 8 requires information to be disclosed. The entity shall report separately information about an operating segment that meets any of the following quantitative thresholds: (a) reported revenue, including both sales to external customers and intersegment sales or transfers, is 10 per cent or more of the combined revenue, internal and external, of all operating segments; (b) the absolute amount of its reported profit or loss is 10 per cent or more of the greater, in absolute amount, of (i) the combined reported profit of all operating segments that did not report a loss and (ii) the combined reported loss of all operating segments that reported a loss; or (c) assets are 10 per cent or more of the combined assets of all operating segments. Additionally operating segments that do not meet any of the quantitative thresholds may be considered reportable, and separately disclosed, if management believes that information about the segment would be useful to users of the financial statements. [Refer: Operating segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FundingArrangementsOfDefinedBenefitPlansAxis' ,N'Funding arrangements of defined benefit plans [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FundingArrangementsOfDefinedBenefitPlansMember' ,N'Funding arrangements of defined benefit plans [member]', N'This member stands for all defined benefit plans when disaggregated by funding arrangements of defined benefits plans. It also represents the standard value for the ''Funding arrangements of defined benefits plans'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WhollyUnfundedDefinedBenefitPlansMember' ,N'Wholly unfunded defined benefit plans [member]', N'This member stands for wholly unfunded defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'WhollyOrPartlyFundedDefinedBenefitPlansMember' ,N'Wholly or partly funded defined benefit plans [member]', N'This member stands for wholly funded or partly funded defined benefit plans. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NetDefinedBenefitLiabilityAssetAxis' ,N'Net defined benefit liability (asset) [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NetDefinedBenefitLiabilityAssetMember' ,N'Net defined benefit liability (asset) [member]', N'This member stands for the net defined benefit liability (asset). It also represents the standard value for the ''Net defined benefit liability (asset)'' axis if no other member is used. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PresentValueOfDefinedBenefitObligationMember' ,N'Present value of defined benefit obligation [member]', N'This member stands for the present value of a defined benefit obligation. The present value of a defined benefit obligation is the present value, without deducting any plan assets, of expected future payments required to settle the obligation resulting from employee service in the current and prior periods.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PlanAssetsMember' ,N'Plan assets [member]', N'This member stands for defined benefit plan assets. Plan assets comprise: (a) assets held by a long-term employee benefit fund; and (b) qualifying insurance policies.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EffectOfAssetCeilingMember' ,N'Effect of asset ceiling [member]', N'This member stands for the effect of asset ceiling in a defined benefit plan. Asset ceiling is the present value of any economic benefits available in the form of refunds from the defined benefit plan or reductions in future contributions to the defined benefit plan. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfNetDefinedBenefitLiabilityAssetLineItems' ,N'Disclosure of net defined benefit liability (asset) [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiabilityAssetOfDefinedBenefitPlans' ,N'Net defined benefit liability (asset)', N'The amount of deficit or surplus in a defined benefit plan, adjusted for any effect of limiting a net defined benefit asset to the asset ceiling. [Refer: Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrentServiceCostNetDefinedBenefitLiabilityAsset' ,N'Current service cost, net defined benefit liability (asset)', N'The increase (decrease) in the net defined benefit liability (asset) resulting from employee service in the current period. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InterestExpenseIncomeNetDefinedBenefitLiabilityAsset' ,N'Interest expense (income), net defined benefit liability (asset)', N'The increase (decrease) in the net defined benefit liability (asset) resulting from the passage of time. [Refer: Interest expense; Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReturnOnPlanAssetsNetDefinedBenefitLiabilityAsset' ,N'Return on plan assets excluding interest income or expense, net defined benefit liability (asset)', N'The decrease (increase) in the net defined benefit liability (asset) resulting from the return on plan assets, excluding amounts included in interest income or expense. The return on plans assets is interest, dividends and other revenue derived from the plan assets, together with realised and unrealised gains or losses on the plan assets, less any costs of managing plan assets and less any tax payable by the plan itself, other than tax included in the actuarial assumptions used to measure the present value of the defined benefit obligation. [Refer: Plan assets [member]; Net defined benefit liability (asset); Actuarial assumptions [member]; Interest expense (income), net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialGainsLossesArisingFromChangesInDemographicAssumptionsNetDefinedBenefitLiabilityAsset' ,N'Actuarial gains (losses) arising from changes in demographic assumptions, net defined benefit liability (asset)', N'The decrease (increase) in a net defined benefit liability (asset) resulting from actuarial gains (losses) arising from changes in demographic assumptions that result in remeasurements of the net defined benefit liability (asset). Demographic assumptions deal with matters such as: (a) mortality; (b) rates of employee turnover, disability and early retirement; (c) the proportion of plan members with dependants who will be eligible for benefits; (d) the proportion of plan members who will select each form of payment option available under the plan terms; and (e) claim rates under medical plans. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialGainsLossesArisingFromChangesInFinancialAssumptionsNetDefinedBenefitLiabilityAsset' ,N'Actuarial gains (losses) arising from changes in financial assumptions, net defined benefit liability (asset)', N'The decrease (increase) in a net defined benefit liability (asset) resulting from actuarial gains (losses) arising from changes in financial assumptions that result in remeasurements of the net defined benefit liability (asset). Financial assumptions deal with items such as: (a) the discount rate; (b) benefit levels, excluding any cost of the benefits to be met by employees, and future salary; (c) in the case of medical benefits, future medical costs, including claim handling costs (ie the costs that will be incurred in processing and resolving claims, including legal and adjuster''s fees); and (d) taxes payable by the plan on contributions relating to service before the reporting date or on benefits resulting from that service. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ActuarialGainsLossesArisingFromExperienceAdjustmentsNetDefinedBenefitLiabilityAsset' ,N'Actuarial gains (losses) arising from experience adjustments, net defined benefit liability (asset)', N'The decrease (increase) in a net defined benefit liability (asset) resulting from actuarial gains (losses) arising from experience adjustments that result in remeasurements of the net defined benefit liability (asset). Experience adjustments deal with the effects of differences between the previous actuarial assumptions and what has actually occurred. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainLossOnChangesInEffectOfLimitingNetDefinedBenefitAssetToAssetCeiling' ,N'Gain (loss) on changes in effect of limiting net defined benefit asset to asset ceiling excluding interest income or expense, net defined benefit liability (asset)', N'The decrease (increase) in the net defined benefit liability (asset) resulting from the gain (loss) on changes in the effect of limiting a net defined benefit asset to the asset ceiling, excluding amounts included in interest income or expense. [Refer: Interest income; Net defined benefit liability (asset); Interest expense (income), net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainLossOnRemeasurementOfNetDefinedBenefitLiabilityAsset' ,N'Gain (loss) on remeasurement, net defined benefit liability (asset)', N'The decrease (increase) in a net defined benefit liability (asset) resulting from the remeasurement of that net defined benefit liability (asset). [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PastServiceCostNetDefinedBenefitLiabilityAsset' ,N'Past service cost, net defined benefit liability (asset)', N'The increase (decrease) in the net defined benefit liability (asset) resulting from past service cost. Past service cost is the change in the present value of the defined benefit obligation for employee service in prior periods, resulting from a plan amendment (the introduction or withdrawal of, or changes to, a defined benefit plan) or a curtailment (a significant reduction by the entity in the number of employees covered by a plan). [Refer: Net defined benefit liability (asset); Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'GainsLossesArisingFromSettlementsNetDefinedBenefitLiabilityAsset' ,N'Gains (losses) arising from settlements, net defined benefit liability (asset)', N'The decrease (increase) in the net defined benefit liability (asset) resulting from settlements. Settlements are transactions that eliminate all further legal or constructive obligations for part or all of the benefits provided under a defined benefit plan, other than a payment of benefits to, or on behalf of, employees that is set out in the terms of the plan and included in the actuarial assumptions. [Refer: Net defined benefit liability (asset); Defined benefit plans [member]; Actuarial assumptions [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PastServiceCostAndGainsLossesArisingFromSettlementsNetDefinedBenefitLiabilityAsset' ,N'Past service cost and gains (losses) arising from settlements, net defined benefit liability (asset)', N'The increase (decrease) in the net defined benefit liability (asset) resulting from past service cost and gains (losses) arising from settlements. [Refer: Gains (losses) arising from settlements, net defined benefit liability (asset); Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseThroughChangesInForeignExchangeRatesNetDefinedBenefitLiabilityAsset' ,N'Increase (decrease) through changes in foreign exchange rates, net defined benefit liability (asset)', N'The increase (decrease) in the net defined benefit liability (asset) resulting from changes in foreign exchange rates. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContributionsToPlanByEmployerNetDefinedBenefitLiabilityAsset' ,N'Contributions to plan by employer, net defined benefit liability (asset)', N'The decrease (increase) in net defined benefit liability (asset) resulting from contributions to a defined benefit plan by the employer. [Refer: Net defined benefit liability (asset); Defined benefit plans [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContributionsToPlanByPlanParticipantsNetDefinedBenefitLiabilityAsset' ,N'Contributions to plan by plan participants, net defined benefit liability (asset)', N'The decrease (increase) in the net defined benefit liability (asset) resulting from contributions to a defined benefit plan by plan participants. [Refer: Net defined benefit liability (asset); Defined benefit plans [member]]');
