	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PaymentsFromPlanNetDefinedBenefitLiabilityAsset' ,N'Payments from plan, net defined benefit liability (asset)', N'The decrease (increase) in the net defined benefit liability (asset) resulting from payments from the plan. [Refer: Net defined benefit liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PaymentsInRespectOfSettlementsNetDefinedBenefitLiabilityAsset' ,N'Payments in respect of settlements, net defined benefit liability (asset)', N'The decrease (increase) in the net defined benefit liability (asset) resulting from payments from the plan in respect of settlements. [Refer: Gains (losses) arising from settlements, net defined benefit liability (asset); Net defined benefit liability (asset); Defined benefit plans [member]]');
