	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncreaseDecreaseInDeferredTaxLiabilityAsset' ,N'Increase (decrease) in deferred tax liability (asset)', N'The increase (decrease) in a deferred tax liability (asset). [Refer: Deferred tax liability (asset)]');
