	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfNatureOfPotentialIncomeTaxConsequencesThatWouldResultFromPaymentOfDividendExplanatory' ,N'Description of nature of potential income tax consequences that would result from payment of dividend', N'The description of the nature of the potential income tax consequences that would result from the payment of dividends to the entity''s shareholders in jurisdictions such as those where income taxes are payable at a higher or lower rate if part or all of the net profit or retained earnings is paid out as a dividend to shareholders of the entity, or where income taxes may be refundable or payable if part or all of the net profit or retained earnings is paid out as a dividend to shareholders of the entity. [Refer: Retained earnings]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfAmountsOfPotentialIncomeTaxConsequencesPracticablyDeterminableExplanatory' ,N'Description of amounts of potential income tax consequences practicably determinable', N'The description of the amounts of the practically determinable potential income tax consequences that would result from the payment of dividends to the entity''s shareholders in jurisdictions where income taxes are payable at a higher or lower rate if part or all of the net profit or retained earnings is paid out as a dividend to shareholders of the entity, or where income taxes may be refundable or payable if part or all of the net profit or retained earnings is paid out as a dividend to shareholders of the entity. [Refer: Retained earnings]');
