	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'QualitativeDescriptionOfEffectOnFinancialStatementsOfChangeInActivitiesThatResultedInInsurerNoLongerQualifyingToApplyTemporaryExemptionFromIfrs9' ,N'Qualitative description of effect on financial statements of change in activities that resulted in insurer no longer qualifying to apply temporary exemption from Ifrs 9', N'Qualitative description of the effect on the financial statements of change in activities that resulted in an insurer no longer qualifying to apply the temporary exemption from Ifrs 9.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssetsDescribedInParagraph39EaOfIfrs4FairValue' ,N'Financial assets described in paragraph 39E(a) of Ifrs 4, fair value', N'The fair value of financial assets described in paragraph 39E(a) of Ifrs 4, ie financial assets with contractual terms that give rise on specified dates to cash flows that are solely payments of principal and interest on the principal amount outstanding (ie financial assets that meet the condition in paragraphs 4.1.2(b) and 4.1.2A(b) of Ifrs 9), excluding any financial asset that meets the definition of held for trading in Ifrs 9, or that is managed and whose performance is evaluated on a fair value basis (paragraph B4.1.6 of Ifrs 9). [Refer: Financial assets]');
