	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssetsOtherThanThoseSpecifiedInParagraph39EaOfIfrs4FairValue' ,N'Financial assets other than those specified in paragraph 39E(a) of Ifrs 4, fair value', N'The fair value of financial assets other than those described in paragraph 39E(a) of Ifrs 4. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
