	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssetsDescribedInParagraph39EaOfIfrs4ThatDoNotHaveLowCreditRiskFairValue' ,N'Financial assets described in paragraph 39E(a) of Ifrs 4 that do not have low credit risk, fair value', N'The fair value of financial assets described in paragraph 39E(a) of Ifrs 4 that do not have low credit risk. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssetsDescribedInParagraph39EaOfIfrs4ThatDoNotHaveLowCreditRiskCarryingAmountApplyingIAS39' ,N'Financial assets described in paragraph 39E(a) of Ifrs 4 that do not have low credit risk, carrying amount applying IAS 39', N'The carrying amount applying IAS 39 of financial assets described in paragraph 39E(a) of Ifrs 4 that do not have low credit risk. In case of financial assets measured at amortised cost, the amount should be before adjusting for any impairment allowances. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutWhereUserOfFinancialStatementsCanObtainAnyPubliclyAvailableIfrs9InformationThatIsNotProvidedInConsolidatedFinancialStatements' ,N'Information about where user of financial statements can obtain any publicly available Ifrs 9 information that is not provided in consolidated financial statements', N'Information about where a user of financial statements can obtain any publicly available Ifrs 9 information that relates to an entity within the group that is not provided in the consolidated financial statements for the relevant reporting period.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfNonderivativeInvestmentContractLiabilitiesMeasuredAtFairValueThroughProfitOrLossApplyingIAS39' ,N'Share of non-derivative investment contract liabilities measured at fair value through profit or loss applying IAS 39', N'The entity''s share of the amount of non-derivative investment contract liabilities measured at fair value through profit or loss applying IAS 39. [Refer: Derivatives [member]]');
