	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfDebtInstrumentsIssuedThatAreIncludedInInsurersRegulatoryCapital' ,N'Share of debt instruments issued that are included in insurer''s regulatory capital', N'The entity''s share of the amount of debt instruments issued that are included in the insurer''s regulatory capital.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfFinancialAssetsDescribedInParagraph39EaOfIfrs4FairValue' ,N'Share of financial assets described in paragraph 39E(a) of Ifrs 4, fair value', N'The entity''s share of the fair value of financial assets described in paragraph 39E(a) of Ifrs 4. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfIncreaseDecreaseInFairValueOfFinancialAssetsDescribedInParagraph39EaOfIfrs4' ,N'Share of increase (decrease) in fair value of financial assets described in paragraph 39E(a) of Ifrs 4', N'The entity''s share of the increase (decrease) in the fair value of financial assets described in paragraph 39E(a) of Ifrs 4. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfFinancialAssetsOtherThanThoseSpecifiedInParagraph39EaOfIfrs4FairValue' ,N'Share of financial assets other than those specified in paragraph 39E(a) of Ifrs 4, fair value', N'The entity''s share of the fair value of financial assets other than those described in paragraph 39E(a) of Ifrs 4. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfIncreaseDecreaseInFairValueOfFinancialAssetsOtherThanThoseSpecifiedInParagraph39EaOfIfrs4' ,N'Share of increase (decrease) in fair value of financial assets other than those specified in paragraph 39E(a) of Ifrs 4', N'The entity''s share of the increase (decrease) in the fair value of financial assets other than those described in paragraph 39E(a) of Ifrs 4. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
