	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfFinancialAssetsDescribedInParagraph39EaOfIfrs4ThatDoNotHaveLowCreditRiskCarryingAmountApplyingIAS39' ,N'Share of financial assets described in paragraph 39E(a) of Ifrs 4 that do not have low credit risk, carrying amount applying IAS 39', N'The entity''s share of the carrying amount applying IAS 39 of financial assets described in paragraph 39E(a) of Ifrs 4 that do not have low credit risk. In case of financial assets measured at amortised cost, the amount should be before adjusting for any impairment allowances. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDetailedInformationAboutFinancialAssetsDescribedInParagraph39EaOfIfrs4ForJointVenturesExplanatory' ,N'Disclosure of detailed information about financial assets described in paragraph 39E(a) of Ifrs 4 for joint ventures [text block]', N'The disclosure of detailed information about the financial assets described in paragraph 39E(a) of Ifrs 4 for joint ventures. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDetailedInformationAboutFinancialAssetsDescribedInParagraph39EaOfIfrs4ForJointVenturesTable' ,N'Disclosure of detailed information about financial assets described in paragraph 39E(a) of Ifrs 4 for joint ventures [table]', N'Schedule disclosing information related to the financial assets described in paragraph 39E(a) of Ifrs 4 for joint ventures.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'JointVenturesAxis' ,N'Joint ventures [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForJointVenturesMember' ,N'Entity''s total for joint ventures [member]', N'This member stands for the standard value for the ''Joint ventures'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'JointVenturesMember' ,N'Joint ventures [member]', N'This member stands for joint arrangements whereby the parties that have joint control of the arrangement have rights to the net assets of the arrangement.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AggregatedIndividuallyImmaterialJointVenturesMember' ,N'Aggregated individually immaterial joint ventures [member]', N'This member stands for the aggregation of joint ventures that are individually immaterial. [Refer: Joint ventures [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExternalCreditGradesAxis' ,N'External credit grades [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForExternalCreditGradesMember' ,N'Entity''s total for external credit grades [member]', N'This member stands for the standard value for the ''External credit grades'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExternalCreditGradesMember' ,N'External credit grades [member]', N'This member stands for credit grades that have been provided by external rating agencies.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InternalCreditGradesAxis' ,N'Internal credit grades [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForInternalCreditGradesMember' ,N'Entity''s total for internal credit grades [member]', N'This member stands for the standard value for the ''Internal credit grades'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InternalCreditGradesMember' ,N'Internal credit grades [member]', N'This member stands for credit grades that have been developed by the entity itself.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfDetailedInformationAboutFinancialAssetsDescribedInParagraph39EaOfIfrs4ForJointVenturesLineItems' ,N'Disclosure of detailed information about financial assets described in paragraph 39E(a) of Ifrs 4 for joint ventures [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FinancialAssetsDescribedInParagraph39EaOfIfrs4CarryingAmountApplyingIAS39' ,N'Financial assets described in paragraph 39E(a) of Ifrs 4, carrying amount applying IAS 39', N'The carrying amount applying IAS 39 of financial assets described in paragraph 39E(a) of Ifrs 4. In case of financial assets measured at amortised cost, the amount should be before adjusting for any impairment allowances. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfFinancialAssetsDescribedInParagraph39EaOfIfrs4CarryingAmountApplyingIAS39' ,N'Share of financial assets described in paragraph 39E(a) of Ifrs 4, carrying amount applying IAS 39', N'The entity''s share of the carrying amount applying IAS 39 of financial assets described in paragraph 39E(a) of Ifrs 4. In case of financial assets measured at amortised cost, the amount should be before adjusting for any impairment allowances. [Refer: Financial assets described in paragraph 39E(a) of Ifrs 4, fair value]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInsuranceContractsExplanatory' ,N'Disclosure of insurance contracts [text block]', N'The entire disclosure for insurance contracts.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceRevenue' ,N'Insurance revenue', N'The amount of revenue arising from the groups of insurance contracts issued. Insurance revenue shall depict the provision of coverage and other services arising from the group of insurance contracts at an amount that reflects the consideration to which the entity expects to be entitled in exchange for those services. [Refer: Insurance contracts issued [member]; Revenue]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceServiceExpensesFromInsuranceContractsIssued' ,N'Insurance service expenses from insurance contracts issued', N'The amount of expenses arising from a group of insurance contracts issued, comprising incurred claims (excluding repayments of investment components), other incurred insurance service expenses, amortisation of insurance acquisition cash flows, changes that relate to past service and changes that relate to future service. [Refer: Insurance contracts issued [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeFromAmountsRecoveredFromReinsurer' ,N'Income from amounts recovered from reinsurer', N'The amount of income from the amounts recovered from the reinsurer. [Refer: Reinsurance contracts held [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpensesFromAllocationOfPremiumsPaidToReinsurer' ,N'Expenses from allocation of premiums paid to reinsurer', N'The amount of expenses from an allocation of the premiums paid to the reinsurer. [Refer: Reinsurance contracts held [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeExpensesFromReinsuranceContractsHeldOtherThanFinanceIncomeExpenses' ,N'Income (expenses) from reinsurance contracts held, other than finance income (expenses)', N'The amount of income (expenses) from a group of reinsurance contracts held, other than finance income (expenses). [Refer: Reinsurance contracts held [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceServiceResult' ,N'Insurance service result', N'The amount comprising insurance revenue and insurance service expenses. [Refer: Insurance revenue; Insurance service expenses from insurance contracts issued; Income (expenses) from reinsurance contracts held, other than finance income (expenses)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AdditionalInformationAboutInsuranceContractsExplanatory' ,N'Additional information about insurance contracts [text block]', N'Additional information about insurance contracts necessary to meet the objective of the disclosure requirements in Ifrs 17. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfCriteriaSatisfiedWhenUsingPremiumAllocationApproach' ,N'Description of criteria satisfied when using premium allocation approach', N'The description of the criteria in paragraphs 53 and 69 of Ifrs 17 that have been satisfied when an entity uses the premium allocation approach. Premium allocation approach is an approach, described in paragraphs 53-59 of Ifrs 17, that simplifies the measurement of the liability for remaining coverage of a group of insurance contracts.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfWhetherEntityMakesAdjustmentForTimeValueOfMoneyAndEffectOfFinancialRiskWhenUsingPremiumAllocationApproach' ,N'Description of whether entity makes adjustment for time value of money and effect of financial risk when using premium allocation approach', N'The description of whether an entity that uses the premium allocation approach makes an adjustment for the time value of money and the effect of financial risk applying paragraphs 56 and 57(b) of Ifrs 17. Premium allocation approach is an approach, described in paragraphs 53-59 of Ifrs 17, that simplifies the measurement of the liability for remaining coverage of a group of insurance contracts.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfMethodToRecogniseInsuranceAcquisitionCashFlowsWhenUsingPremiumAllocationApproach' ,N'Description of method to recognise insurance acquisition cash flows when using premium allocation approach', N'The description of the method chosen by an entity that uses the premium allocation approach to recognise insurance acquisition cash flows applying paragraph 59(a) of Ifrs 17. Premium allocation approach is an approach, described in paragraphs 53-59 of Ifrs 17, that simplifies the measurement of the liability for remaining coverage of a group of insurance contracts. [Refer: Increase (decrease) through insurance acquisition cash flows, insurance contracts liability (asset)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfChangesInInsuranceContractsByRemainingCoverageAndIncurredClaimsExplanatory' ,N'Disclosure of reconciliation of changes in insurance contracts by remaining coverage and incurred claims [text block]', N'The disclosure of the reconciliation of changes in insurance contracts by remaining coverage and incurred claims. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReconciliationOfChangesInInsuranceContractsByRemainingCoverageAndIncurredClaimsTable' ,N'Disclosure of reconciliation of changes in insurance contracts by remaining coverage and incurred claims [table]', N'Schedule disclosing information related to the reconciliation of changes in insurance contracts by remaining coverage and incurred claims.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisaggregationOfInsuranceContractsAxis' ,N'Disaggregation of insurance contracts [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisaggregationOfInsuranceContractsMember' ,N'Disaggregation of insurance contracts [member]', N'This member stands for all insurance contracts when disaggregated into insurance contracts issued and reinsurance contracts held. It also represents the standard value for the ''Disaggregation of insurance contracts'' axis if no other member is used. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceContractsIssuedMember' ,N'Insurance contracts issued [member]', N'This member stands for insurance contracts issued. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReinsuranceContractsHeldMember' ,N'Reinsurance contracts held [member]', N'This member stands for reinsurance contracts held. Reinsurance contracts are insurance contracts issued by one entity (the reinsurer) to compensate another entity for claims arising from one or more insurance contracts issued by that other entity (underlying contracts). [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceContractsByRemainingCoverageAndIncurredClaimsAxis' ,N'Insurance contracts by remaining coverage and incurred claims [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceContractsByRemainingCoverageAndIncurredClaimsMember' ,N'Insurance contracts by remaining coverage and incurred claims [member]', N'This member stands for all insurance contracts when disaggregated by remaining coverage and incurred claims. This member also represents the standard value for the ''Insurance contracts by remaining coverage and incurred claims'' axis if no other member is used. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NetLiabilitiesOrAssetsForRemainingCoverageExcludingLossComponentMember' ,N'Net liabilities or assets for remaining coverage excluding loss component [member]', N'This member stands for the net liabilities or assets for the remaining coverage component, excluding any loss component. A liability for remaining coverage is an entity’s obligation to investigate and pay valid claims under existing insurance contracts for insured events that have not yet occurred (ie the obligation that relates to the unexpired portion of the coverage period). [Refer: Insurance contracts [member]; Loss component [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LossComponentMember' ,N'Loss component [member]', N'This member stands for the loss component of the liability for remaining coverage. An entity shall establish (or increase) a loss component of the liability for remaining coverage for an onerous group depicting the losses recognised applying paragraphs 47–48 of Ifrs 17. The loss component determines the amounts that are presented in profit or loss as reversals of losses on onerous groups and are consequently excluded from the determination of insurance revenue. [Refer: Insurance contracts [member]; Net liabilities or assets for remaining coverage excluding loss component [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiabilitiesForIncurredClaimsMember' ,N'Liabilities for incurred claims [member]', N'This member stands for an entity’s obligation to investigate and pay valid claims for insured events that have already occurred, including events that have occurred but for which claims have not been reported, and other incurred insurance expenses.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceContractsByComponentsAxis' ,N'Insurance contracts by components [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InsuranceContractsByComponentsMember' ,N'Insurance contracts by components [member]', N'This member stands for all insurance contracts when disaggregated by components, ie the estimates of the present value of the future cash flows, the risk adjustment for non-financial risk and the contractual service margin. This member also represents the standard value for the ''Insurance contracts by components'' axis if no other member is used. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EstimatesOfPresentValueOfFutureCashFlowsMember' ,N'Estimates of present value of future cash flows [member]', N'This member stands for the estimates of the present value of the future cash flows related to insurance contracts. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EstimatesOfPresentValueOfFutureCashOutflowsMember' ,N'Estimates of present value of future cash outflows [member]', N'This member stands for the estimates of the present value of future cash flows outflows related to insurance contracts. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EstimatesOfPresentValueOfInsuranceAcquisitionCashFlowsMember' ,N'Estimates of present value of insurance acquisition cash flows [member]', N'This member stands for the estimates of the present value of the insurance acquisition cash flows. Insurance acquisition cash flows are cash flows arising from the costs of selling, underwriting and starting a group of insurance contracts that are directly attributable to the portfolio of insurance contracts to which the group belongs. Such cash flows include cash flows that are not directly attributable to individual contracts or groups of insurance contracts within the portfolio.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EstimatesOfPresentValueOfFutureCashOutflowsOtherThanInsuranceAcquisitionCashFlowsMember' ,N'Estimates of present value of future cash outflows other than insurance acquisition cash flows [member]', N'This member stands for the estimates of the present value of future cash outflows other than the insurance acquisition cash flows. [Refer: Estimates of present value of insurance acquisition cash flows [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EstimatesOfPresentValueOfFutureCashInflowsMember' ,N'Estimates of present value of future cash inflows [member]', N'This member stands for the estimates of the present value of future cash flows inflows related to insurance contracts. [Refer: Insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RiskAdjustmentForNonfinancialRiskMember' ,N'Risk adjustment for non-financial risk [member]', N'This member stands for the compensation an entity requires for bearing the uncertainty about the amount and timing of the cash flows that arises from non-financial risk as the entity fulfils insurance contracts.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContractualServiceMarginMember' ,N'Contractual service margin [member]', N'This member stands for a component of the carrying amount of the asset or liability for a group of insurance contracts representing the unearned profit the entity will recognise as it provides services under the insurance contracts in the group.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ContractualServiceMarginRelatedToContractsThatExistedAtTransitionDateToWhichModifiedRetrospectiveApproachHasBeenAppliedMember' ,N'Contractual service margin related to contracts that existed at transition date to which modified retrospective approach has been applied [member]', N'This member stands for the contractual service margin related to contracts that existed at the transition date to which the modified retrospective approach (as described in paragraphs C6-C19 of Ifrs 17) has been applied. [Refer: Contractual service margin [member]]');
