	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LaterThanFourYearsAndNotLaterThanFiveYearsMember' ,N'Later than four years and not later than five years [member]', N'This member stands for a time band of later than four years and not later than five years.');
