	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfEffectOfChangesInForeignExchangeRatesExplanatory' ,N'Disclosure of effect of changes in foreign exchange rates [text block]', N'The entire disclosure for the effect of changes in foreign exchange rates.');
